// Áªü‰∏ÄÊï∞ÊçÆÊúçÂä° - Áé∞Âú®‰ΩøÁî®ÂêéÁ´ØAPIËÄå‰∏çÊòØlocalStorage
// src/services/dataService.js

import apiService from './apiService';

export class DataService {
  // ÁºìÂ≠òÊï∞ÊçÆ
  static cache = {
    categories: null,
    users: null,
    requests: null,
    shortlists: null,
    lastFetch: {}
  };

  // ÁºìÂ≠òÊúâÊïàÊúüÔºà5ÂàÜÈíüÔºâ
  static CACHE_DURATION = 5 * 60 * 1000;

  // Ê£ÄÊü•ÁºìÂ≠òÊòØÂê¶ÊúâÊïà
  static isCacheValid(key) {
    const lastFetch = this.cache.lastFetch[key];
    return lastFetch && (Date.now() - lastFetch) < this.CACHE_DURATION;
  }

  // Êõ¥Êñ∞ÁºìÂ≠ò
  static updateCache(key, data) {
    this.cache[key] = data;
    this.cache.lastFetch[key] = Date.now();
  }

  // Ê∏ÖÁ©∫ÁºìÂ≠ò
  static clearCache(key = null) {
    if (key) {
      this.cache[key] = null;
      delete this.cache.lastFetch[key];
    } else {
      this.cache = {
        categories: null,
        users: null,
        requests: null,
        shortlists: null,
        lastFetch: {}
      };
    }
  }

  // ÂàÜÁ±ªÊï∞ÊçÆ
  static async getCategories() {
    try {
      // Ê£ÄÊü•ÁºìÂ≠ò
      if (this.isCacheValid('categories') && this.cache.categories) {
        return this.cache.categories;
      }

      const categories = await apiService.getCategories();
      
      // Á°Æ‰øùcategoriesÊòØÊï∞ÁªÑ
