        throw new Error('Ëé∑ÂèñÁöÑÂàÜÁ±ªÊï∞ÊçÆÊ†ºÂºè‰∏çÊ≠£Á°Æ');
      }
      
      // ËΩ¨Êç¢Êï∞ÊçÆÊ†ºÂºè‰ª•ÂÖºÂÆπÁé∞Êúâ‰ª£Á†Å
      const formattedCategories = categories.map(cat => ({
        id: cat.name || cat._id,
        name: `category.${cat.name}`,
        icon: cat.icon || 'üìÅ',
        color: this.getColorFromHex(cat.color || '#42a5f5')
      }));

      this.updateCache('categories', formattedCategories);
      return formattedCategories;
    } catch (error) {
      console.error('Ëé∑ÂèñÂàÜÁ±ªÂ§±Ë¥•Ôºå‰ΩøÁî®ÈªòËÆ§Êï∞ÊçÆ:', error);
      // ‰ΩøÁî®ÈªòËÆ§Êï∞ÊçÆ‰Ωú‰∏∫ÂêéÂ§á
      const defaultCategories = [
        { id: 'medical', name: 'category.medical', icon: 'üè•' },
        { id: 'transportation', name: 'category.transport', icon: 'üöó' },
        { id: 'shopping', name: 'category.shopping', icon: 'üõí' },
        { id: 'household', name: 'category.household', icon: 'üè†' },
        { id: 'companion', name: 'category.companion', icon: 'üë•' },
        { id: 'technology', name: 'category.technology', icon: 'üíª' },
        { id: 'education', name: 'category.education', icon: 'üìö' },
