  // Ê†πÊçÆÁ¥ßÊÄ•Á®ãÂ∫¶IDËé∑ÂèñÁ¥ßÊÄ•Á®ãÂ∫¶
  static getUrgencyById(urgencyId) {
    const urgencyLevels = this.getUrgencyLevels();
