    const loadDashboardData = async () => {
      try {
        // ÂàùÂßãÂåñÁ§∫‰æãÊï∞ÊçÆ
        await DataService.initializeData();
        
        const statistics = await DataService.getStatistics();
        const users = await DataService.getUsers();
        const requests = await DataService.getRequests();
        
        // ËÆ°ÁÆóÊú¨ÊúàÊñ∞Â¢û
        const currentMonth = new Date().getMonth();
        const currentYear = new Date().getFullYear();
        
        const thisMonthUsers = (users || []).filter(user => {
          const userDate = new Date(user.registeredAt || user.createdAt || Date.now());
          return userDate.getMonth() === currentMonth && userDate.getFullYear() === currentYear;
        });
        
        const thisMonthRequests = (requests || []).filter(request => {
          const requestDate = new Date(request.createdAt || Date.now());
          return requestDate.getMonth() === currentMonth && requestDate.getFullYear() === currentYear;
        });
        
        // ‰ªäÊó•ÂåπÈÖç
        const today = new Date();
        today.setHours(0, 0, 0, 0);
        const todayMatched = (requests || []).filter(request => {
          if (request.status !== 'matched') return false;
          const matchDate = new Date(request.matchedAt || request.updatedAt || request.createdAt);
          matchDate.setHours(0, 0, 0, 0);
          return matchDate.getTime() === today.getTime();
        });
        
        const stats = [
          {
            title: t('admin.dashboard.totalUsers'),
            value: statistics.totalUsers.toString(),
            change: `+${thisMonthUsers.length}`,
            trend: thisMonthUsers.length > 0 ? 'up' : 'neutral',
            icon: 'üë•',
            color: 'primary'
          },
          {
            title: t('admin.dashboard.activeRequests'),
            value: statistics.activeRequests.toString(),
            change: `+${thisMonthRequests.length}`,
            trend: thisMonthRequests.length > 0 ? 'up' : 'neutral',
            icon: 'üìã',
            color: 'secondary'
          },
          {
            title: t('admin.dashboard.todayMatches'),
            value: todayMatched.length.toString(),
            change: `+${statistics.matchedRequests}`,
            trend: statistics.matchedRequests > 0 ? 'up' : 'neutral',
            icon: '‚úÖ',
            color: 'success'
          },
          {
            title: t('admin.dashboard.pendingReview'),
            value: requests.filter(r => r.status === 'pending').length.toString(),
            change: '0',
            trend: 'neutral',
            icon: '‚è≥',
            color: 'warning'
          }
        ];
        
        // ÁîüÊàêÊúÄËøëÊ¥ªÂä®
        const activities = [];
        
        // ÊúÄËøëÊ≥®ÂÜåÁöÑÁî®Êà∑
        const recentUsers = users
          .sort((a, b) => new Date(b.registeredAt || b.createdAt || 0) - new Date(a.registeredAt || a.createdAt || 0))
          .slice(0, 2);
        
        recentUsers.forEach(user => {
          activities.push({
            icon: 'üë§',
            content: `${t('common.newUser')} - ${user.name}`,
            time: DataService.getTimeAgo(user.registeredAt || user.createdAt, t)
          });
        });
        
        // ÊúÄËøëÁöÑËØ∑Ê±Ç
        const recentRequests = requests
          .sort((a, b) => new Date(b.createdAt || 0) - new Date(a.createdAt || 0))
          .slice(0, 2);
        
        // Ëé∑ÂèñÊâÄÊúâÂàÜÁ±ª‰ª•‰æøÂêéÁª≠Êü•Êâæ
        const allCategories = await DataService.getCategories();
        
        recentRequests.forEach(request => {
          const icon = request.status === 'matched' ? '‚úÖ' : 'üìã';
          const category = (allCategories || []).find(cat => cat.id === request.category);
          const categoryName = category ? t(category.name) : request.category;
          const content = request.status === 'matched' 
            ? `${categoryName}${t('common.requestMatched')}`
            : `${t('common.newRequest')} - ${categoryName}`;
          
          activities.push({
            icon,
            content,
            time: DataService.getTimeAgo(request.createdAt, t)
          });
        });
        
        setDashboardData({ stats, activities });
        setLastUpdated(new Date());
      } catch (error) {
        console.error('Âä†ËΩΩ‰ª™Ë°®ÊùøÊï∞ÊçÆÂ§±Ë¥•:', error);
        setDashboardData({
          stats: [
            { title: t('admin.dashboard.totalUsers'), value: '0', change: '+0', trend: 'neutral', icon: 'üë•', color: 'primary' },
            { title: t('admin.dashboard.activeRequests'), value: '0', change: '+0', trend: 'neutral', icon: 'üìã', color: 'secondary' },
            { title: t('admin.dashboard.todayMatches'), value: '0', change: '+0', trend: 'neutral', icon: '‚úÖ', color: 'success' },
            { title: t('admin.dashboard.pendingReview'), value: '0', change: '+0', trend: 'neutral', icon: '‚è≥', color: 'warning' }
          ],
          activities: []
        });
      } finally {
        setLoading(false);
      }
    };
    
    loadDashboardData();
    
    // ÂÆöÊó∂Âà∑Êñ∞
    const interval = setInterval(loadDashboardData, 30000);
    return () => clearInterval(interval);
  }, [t]);

  const quickActions = [
    { title: t('admin.dashboard.userManagement'), icon: 'üë•', path: '/admin/users', color: 'blue' },
    { title: t('admin.dashboard.serviceCategories'), icon: 'üìÅ', path: '/admin/categories', color: 'green' },
    { title: t('admin.dashboard.dataReports'), icon: 'üìä', path: '/admin/reports', color: 'purple' },
    { title: t('admin.dashboard.systemSettings'), icon: '‚öôÔ∏è', path: '/admin/settings', color: 'gray' }
  ];

  return (
    <div className="page-container">
      <Navbar userType="admin" user={user} />
      <div className="main-content">
        <div className="dashboard">
          <div className="dashboard-header">
            <div className="header-content">
              <h1 className="dashboard-title">{t('admin.dashboard.title')}</h1>
              <p className="dashboard-subtitle">{t('admin.dashboard.subtitle', { name: user?.name })}</p>
              {lastUpdated && (
                <div className="data-fresh-indicator fresh">
                  <span>üîÑ</span>
                  <span>{t('time.updatedAt')}: {lastUpdated.toLocaleTimeString()}</span>
                </div>
              )}
            </div>
            <div className="header-actions">
              <button 
                className="btn btn-secondary"
                onClick={() => {
                  setLoading(true);
                  setTimeout(() => {
                    // ÈáçÊñ∞Âä†ËΩΩÊï∞ÊçÆÁöÑÈÄªËæë‰ºöÂú®useEffect‰∏≠Â§ÑÁêÜ
                    window.location.reload(); // ÁÆÄÂçïÁöÑÂà∑Êñ∞ÊñπÂºè
                  }, 500);
                }}
                disabled={loading}
              >
                <span className="btn-icon">üîÑ</span>
                {loading ? t('common.refreshing') : t('admin.dashboard.refreshData')}
              </button>
              <button 
                className="btn btn-primary"
                onClick={async () => {
                  try {
                    // ÁîüÊàêÂπ∂‰∏ãËΩΩÁÆÄÂçïÁöÑJSONÊä•Âëä
                    const statistics = await DataService.getStatistics();
                    const users = await DataService.getUsers();
                    const requests = await DataService.getRequests();
                    
                    const report = {
                      title: 'System Report',
                      generatedAt: new Date().toISOString(),
                      statistics,
                      summary: {
                        totalUsers: (users || []).length,
                        totalRequests: (requests || []).length,
                        activeRequests: (requests || []).filter(r => r.status === 'pending' || r.status === 'matched').length,
                        matchedRequests: (requests || []).filter(r => r.status === 'matched').length
                      }
                    };
                    
                    const dataStr = JSON.stringify(report, null, 2);
                    const dataBlob = new Blob([dataStr], { type: 'application/json' });
                    const link = document.createElement('a');
                    link.href = URL.createObjectURL(dataBlob);
                    link.download = `system_report_${new Date().toISOString().split('T')[0]}.json`;
                    document.body.appendChild(link);
                    link.click();
                    document.body.removeChild(link);
                    
                    alert(t('admin.dashboard.reportGenerated'));
                  } catch (error) {
                    console.error('ÁîüÊàêÊä•ÂëäÂ§±Ë¥•:', error);
                    alert(t('admin.dashboard.reportError'));
                  }
                }}
              >
                <span className="btn-icon">üìä</span>
                {t('admin.dashboard.generateReport')}
              </button>
            </div>
          </div>

          <div className="stats-grid">
            {loading ? (
              // Âä†ËΩΩÁä∂ÊÄÅ
              Array.from({ length: 4 }).map((_, index) => (
                <div key={index} className="stat-card loading">
                  <div className="stat-header">
                    <div className="stat-info">
                      <div className="stat-title">{t('common.loading')}</div>
                      <div className="stat-value">--</div>
                    </div>
                    <div className="stat-icon">‚è≥</div>
                  </div>
                  <div className="stat-change">-- --</div>
                </div>
              ))
            ) : (
              dashboardData.stats.map((stat, index) => (
                <div key={index} className={`stat-card ${stat.color}`}>
                  <div className="stat-header">
                    <div className="stat-info">
                      <div className="stat-title">{stat.title}</div>
                      <div className="stat-value">{stat.value}</div>
                    </div>
                    <div className={`stat-icon ${stat.color}`}>
                      {stat.icon}
                    </div>
                  </div>
                  <div className={`stat-change ${stat.trend}`}>
                    {stat.trend === 'up' ? '‚Üó' : stat.trend === 'down' ? '‚Üò' : '‚Üí'} 
                    {stat.change} {t('admin.dashboard.thisMonth')}
                  </div>
                </div>
              ))
            )}
          </div>

          <div className="content-grid">
            <div className="card">
              <div className="card-header">
                <h3 className="card-title">{t('admin.dashboard.quickActions')}</h3>
              </div>
              <div className="card-body">
                <div className="actions-grid">
                  {quickActions.map((action, index) => (
                    <Link key={index} to={action.path} className="action-card">
                      <div className={`action-icon ${action.color}`}>
                        {action.icon}
                      </div>
                      <div className="action-title">{action.title}</div>
                    </Link>
                  ))}
                </div>
              </div>
            </div>

            <div className="card">
              <div className="card-header">
                <h3 className="card-title">{t('admin.dashboard.recentActivity')}</h3>
              </div>
              <div className="card-body">
                {loading ? (
                  <div className="activity-list">
                    <div className="activity-item loading">
                      <div className="activity-icon">‚è≥</div>
                      <div className="activity-content">
                        <p>{t('common.loading')}</p>
                        <span className="activity-time">--</span>
                      </div>
                    </div>
                  </div>
                ) : dashboardData.activities.length > 0 ? (
                  <div className="activity-list">
                    {dashboardData.activities.map((activity, index) => (
                      <div key={index} className="activity-item">
                        <div className="activity-icon">{activity.icon}</div>
                        <div className="activity-content">
                          <p>{activity.content}</p>
                          <span className="activity-time">{activity.time}</span>
                        </div>
                      </div>
                    ))}
                  </div>
                ) : (
                  <div className="activity-list">
                    <div className="activity-item">
                      <div className="activity-icon">üìã</div>
                      <div className="activity-content">
                        <p>{t('admin.dashboard.noActivity')}</p>
                        <span className="activity-time">--</span>
                      </div>
                    </div>
                  </div>
                )}
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AdminDashboard;
