// PINç”¨æˆ·ä»ªè¡¨æ¿
// src/pages/PIN/Dashboard.jsx
import React, { useState } from 'react';
import { useAuth } from '../../context/AuthContext';
import Navbar from '../../components/Layout/Navbar';
import CreateRequestForm from '../../components/PIN/CreateRequestForm';

const PINDashboard = () => {
  const { user } = useAuth();
  const [showCreateForm, setShowCreateForm] = useState(false);
  
  // ä»localStorageè·å–è¯¥ç”¨æˆ·çš„è¯·æ±‚
  const getUserRequests = () => {
    const allRequests = JSON.parse(localStorage.getItem('userRequests') || '[]');
    return allRequests.filter(request => request.requesterId === user?.id);
  };
  
  const [requests, setRequests] = useState(getUserRequests());

  const handleCreateRequest = (newRequest) => {
    // æ›´æ–°æœ¬åœ°çŠ¶æ€
    setRequests(prev => [newRequest, ...prev]);
    
    // ä¿å­˜åˆ°localStorage
    const allRequests = JSON.parse(localStorage.getItem('userRequests') || '[]');
    const updatedRequests = [newRequest, ...allRequests];
    localStorage.setItem('userRequests', JSON.stringify(updatedRequests));
  };
  
  return (
    <div className="page-container">
      <Navbar userType="pin" user={user} />
      <div className="main-content">
        <div className="dashboard">
