// System Logs - Á≥ªÁªüÊó•ÂøóÈ°µÈù¢ (System Admin Only)
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import { useLanguage } from '../../context/LanguageContext';
import Navbar from '../../components/Layout/Navbar';
import { isSystemAdmin } from '../../utils/permissions';
import apiService from '../../services/apiService';
import '../../styles/user-management.css';

const SystemLogs = () => {
  const navigate = useNavigate();
  const { user } = useAuth();
  const { t } = useLanguage();
  const [logs, setLogs] = useState([]);
  const [filteredLogs, setFilteredLogs] = useState([]);
  const [filterType, setFilterType] = useState('all');
  const [searchTerm, setSearchTerm] = useState('');
  const [loading, setLoading] = useState(true);

  // ÊùÉÈôêÊ£ÄÊü• - Âè™Êúâ System Admin ÂèØ‰ª•ËÆøÈóÆ
  useEffect(() => {
    if (!isSystemAdmin(user)) {
      alert(t('permission.denied') || 'Access Denied: System Admin only');
      navigate('/admin/dashboard');
    }
  }, [user, navigate, t]);

  // Ê®°ÊãüÊó•ÂøóÊï∞ÊçÆ
  useEffect(() => {
    loadSystemLogs();
  }, []);

  const loadSystemLogs = () => {
    setLoading(true);
    
    // Ê®°ÊãüÁ≥ªÁªüÊó•ÂøóÊï∞ÊçÆ
    const mockLogs = [
      {
        id: 1,
        type: 'login',
        action: 'User Login',
        user: 'john.doe@example.com',
        userType: 'pin',
        timestamp: new Date(Date.now() - 1000 * 60 * 5).toISOString(),
        status: 'success',
        ipAddress: '192.168.1.100',
        details: 'Successful login from Chrome browser'
      },
      {
        id: 2,
        type: 'login',
        action: 'Failed Login Attempt',
        user: 'unknown@example.com',
        userType: 'unknown',
        timestamp: new Date(Date.now() - 1000 * 60 * 15).toISOString(),
        status: 'failed',
        ipAddress: '192.168.1.105',
        details: 'Invalid credentials - 3 attempts'
      },
      {
        id: 3,
        type: 'system',
        action: 'User Account Created',
        user: 'admin@system.com',
        userType: 'system_admin',
        timestamp: new Date(Date.now() - 1000 * 60 * 30).toISOString(),
        status: 'success',
        ipAddress: '192.168.1.1',
        details: 'Created new CSR account: jane.smith@example.com'
      },
      {
        id: 4,
        type: 'system',
        action: 'User Account Suspended',
        user: 'admin@system.com',
        userType: 'system_admin',
        timestamp: new Date(Date.now() - 1000 * 60 * 45).toISOString(),
        status: 'success',
        ipAddress: '192.168.1.1',
        details: 'Suspended user account: spam.user@example.com - Reason: Policy violation'
      },
      {
        id: 5,
        type: 'login',
        action: 'User Login',
        user: 'volunteer@example.com',
        userType: 'csr',
        timestamp: new Date(Date.now() - 1000 * 60 * 60).toISOString(),
        status: 'success',
        ipAddress: '192.168.1.102',
        details: 'Successful login from Firefox browser'
      },
      {
        id: 6,
        type: 'security',
        action: 'Password Reset',
        user: 'user@example.com',
        userType: 'pin',
        timestamp: new Date(Date.now() - 1000 * 60 * 90).toISOString(),
        status: 'success',
        ipAddress: '192.168.1.103',
        details: 'Password reset via email verification'
      },
      {
        id: 7,
        type: 'login',
        action: 'Failed Login Attempt',
        user: 'test@example.com',
        userType: 'unknown',
        timestamp: new Date(Date.now() - 1000 * 60 * 120).toISOString(),
        status: 'failed',
        ipAddress: '192.168.1.106',
        details: 'Account locked after 5 failed attempts'
      },
      {
        id: 8,
        type: 'system',
        action: 'Database Backup',
        user: 'system',
        userType: 'system',
        timestamp: new Date(Date.now() - 1000 * 60 * 180).toISOString(),
        status: 'success',
        ipAddress: 'localhost',
        details: 'Automated daily backup completed successfully'
      }
    ];

    setLogs(mockLogs);
    setFilteredLogs(mockLogs);
    setLoading(false);
  };

  // ËøáÊª§Êó•Âøó
  useEffect(() => {
    let filtered = logs;

    // Á±ªÂûãËøáÊª§
    if (filterType !== 'all') {
      filtered = filtered.filter(log => log.type === filterType);
    }

    // ÊêúÁ¥¢ËøáÊª§
    if (searchTerm) {
      filtered = filtered.filter(log =>
        log.user.toLowerCase().includes(searchTerm.toLowerCase()) ||
        log.action.toLowerCase().includes(searchTerm.toLowerCase()) ||
        log.details.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    setFilteredLogs(filtered);
  }, [filterType, searchTerm, logs]);

  const getStatusColor = (status) => {
    return status === 'success' ? 'status-active' : 'status-suspended';
  };

  const getTypeColor = (type) => {
    const colors = {
      login: 'user-type-badge primary',
      security: 'user-type-badge warning',
      system: 'user-type-badge secondary',
    };
    return colors[type] || 'user-type-badge';
  };

  const formatTimestamp = (timestamp) => {
    const date = new Date(timestamp);
    return date.toLocaleString('zh-CN', {
      year: 'numeric',
      month: '2-digit',
      day: '2-digit',
      hour: '2-digit',
      minute: '2-digit',
      second: '2-digit'
    });
  };

  const getTimeAgo = (timestamp) => {
    const now = Date.now();
    const diff = now - new Date(timestamp).getTime();
    const minutes = Math.floor(diff / 60000);
    const hours = Math.floor(minutes / 60);
    const days = Math.floor(hours / 24);

    if (days > 0) return `${days}Â§©Ââç`;
    if (hours > 0) return `${hours}Â∞èÊó∂Ââç`;
    if (minutes > 0) return `${minutes}ÂàÜÈíüÂâç`;
    return 'ÂàöÂàö';
  };

  return (
    <div className="user-management-container">
      <Navbar userType={user?.userType} user={user} />
      
      <div className="user-management-content">
        <div className="page-header">
          <div className="header-content">
            <button onClick={() => navigate('/admin/dashboard')} className="back-button">
              ‚Üê {t('common.back') || 'ËøîÂõû'}
            </button>
            <div className="header-text">
              <h1 className="page-title">
                üìã {t('admin.systemLogs.title') || 'Á≥ªÁªüÊó•Âøó'}
              </h1>
              <p className="page-subtitle">
                {t('admin.systemLogs.subtitle') || 'ÁõëÊéßÁ≥ªÁªüÊ¥ªÂä®ÂíåÁôªÂΩïÂ∞ùËØï'}
              </p>
            </div>
          </div>
        </div>

        {/* ÁªüËÆ°Âç°Áâá */}
        <div className="stats-grid">
          <div className="stat-card">
            <div className="stat-icon">üìä</div>
            <div className="stat-content">
              <div className="stat-label">{t('admin.systemLogs.totalLogs') || 'ÊÄªÊó•ÂøóÊï∞'}</div>
              <div className="stat-value">{logs.length}</div>
            </div>
          </div>
          <div className="stat-card">
            <div className="stat-icon">‚úÖ</div>
            <div className="stat-content">
              <div className="stat-label">{t('admin.systemLogs.successfulLogins') || 'ÊàêÂäüÁôªÂΩï'}</div>
              <div className="stat-value">
                {logs.filter(l => l.type === 'login' && l.status === 'success').length}
              </div>
            </div>
          </div>
          <div className="stat-card">
            <div className="stat-icon">‚ùå</div>
            <div className="stat-content">
              <div className="stat-label">{t('admin.systemLogs.failedAttempts') || 'Â§±Ë¥•Â∞ùËØï'}</div>
              <div className="stat-value">
                {logs.filter(l => l.status === 'failed').length}
              </div>
            </div>
          </div>
          <div className="stat-card">
            <div className="stat-icon">üîß</div>
            <div className="stat-content">
              <div className="stat-label">{t('admin.systemLogs.systemActions') || 'Á≥ªÁªüÊìç‰Ωú'}</div>
              <div className="stat-value">
                {logs.filter(l => l.type === 'system').length}
              </div>
            </div>
          </div>
        </div>

        {/* ËøáÊª§ÂíåÊêúÁ¥¢ */}
        <div className="filters-section">
          <div className="filter-tabs">
            <button
              className={`filter-tab ${filterType === 'all' ? 'active' : ''}`}
              onClick={() => setFilterType('all')}
            >
              {t('common.all') || 'ÂÖ®ÈÉ®'} ({logs.length})
            </button>
            <button
              className={`filter-tab ${filterType === 'login' ? 'active' : ''}`}
              onClick={() => setFilterType('login')}
            >
              üîê {t('admin.systemLogs.loginLogs') || 'ÁôªÂΩïÊó•Âøó'} ({logs.filter(l => l.type === 'login').length})
            </button>
            <button
              className={`filter-tab ${filterType === 'security' ? 'active' : ''}`}
              onClick={() => setFilterType('security')}
            >
              üîí {t('admin.systemLogs.securityLogs') || 'ÂÆâÂÖ®Êó•Âøó'} ({logs.filter(l => l.type === 'security').length})
            </button>
            <button
              className={`filter-tab ${filterType === 'system' ? 'active' : ''}`}
              onClick={() => setFilterType('system')}
            >
              ‚öôÔ∏è {t('admin.systemLogs.systemLogs') || 'Á≥ªÁªüÊó•Âøó'} ({logs.filter(l => l.type === 'system').length})
            </button>
          </div>

          <div className="search-box">
            <span className="search-icon">üîç</span>
            <input
              type="text"
              placeholder={t('admin.systemLogs.searchPlaceholder') || 'ÊêúÁ¥¢Áî®Êà∑„ÄÅÊìç‰ΩúÊàñËØ¶ÊÉÖ...'}
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="search-input"
            />
          </div>
        </div>

        {/* Êó•ÂøóË°®Ê†º */}
        <div className="table-container">
          {loading ? (
            <div className="loading-state">
              <div className="loading-spinner">‚è≥</div>
              <p>{t('common.loading') || 'Âä†ËΩΩ‰∏≠...'}</p>
            </div>
          ) : filteredLogs.length === 0 ? (
            <div className="empty-state">
              <div className="empty-icon">üìã</div>
              <h3>{t('admin.systemLogs.noLogs') || 'ÊöÇÊó†Êó•Âøó'}</h3>
              <p>{t('admin.systemLogs.noLogsDesc') || 'Ê≤°ÊúâÊâæÂà∞ÂåπÈÖçÁöÑÊó•ÂøóËÆ∞ÂΩï'}</p>
            </div>
          ) : (
            <table className="data-table">
              <thead>
                <tr>
                  <th>{t('admin.systemLogs.time') || 'Êó∂Èó¥'}</th>
                  <th>{t('admin.systemLogs.type') || 'Á±ªÂûã'}</th>
                  <th>{t('admin.systemLogs.action') || 'Êìç‰Ωú'}</th>
                  <th>{t('admin.systemLogs.user') || 'Áî®Êà∑'}</th>
                  <th>{t('admin.systemLogs.status') || 'Áä∂ÊÄÅ'}</th>
                  <th>{t('admin.systemLogs.ipAddress') || 'IPÂú∞ÂùÄ'}</th>
                  <th>{t('admin.systemLogs.details') || 'ËØ¶ÊÉÖ'}</th>
                </tr>
              </thead>
              <tbody>
                {filteredLogs.map((log) => (
                  <tr key={log.id}>
                    <td>
                      <div className="timestamp-cell">
                        <div>{formatTimestamp(log.timestamp)}</div>
                        <small style={{ color: '#666' }}>{getTimeAgo(log.timestamp)}</small>
                      </div>
                    </td>
                    <td>
                      <span className={getTypeColor(log.type)}>
                        {log.type === 'login' ? 'üîê ÁôªÂΩï' : log.type === 'security' ? 'üîí ÂÆâÂÖ®' : '‚öôÔ∏è Á≥ªÁªü'}
                      </span>
                    </td>
                    <td>{log.action}</td>
                    <td>
                      <div>
                        {log.user}
                        {log.userType !== 'unknown' && log.userType !== 'system' && (
                          <div style={{ fontSize: '0.85em', color: '#666' }}>
                            {log.userType === 'pin' ? 'PIN' : log.userType === 'csr' ? 'CSR' : log.userType}
                          </div>
                        )}
                      </div>
                    </td>
                    <td>
                      <span className={`status-badge ${getStatusColor(log.status)}`}>
                        {log.status === 'success' ? '‚úÖ ÊàêÂäü' : '‚ùå Â§±Ë¥•'}
                      </span>
                    </td>
                    <td><code>{log.ipAddress}</code></td>
                    <td className="details-cell">{log.details}</td>
                  </tr>
                ))}
              </tbody>
            </table>
          )}
        </div>
      </div>
    </div>
  );
};

export default SystemLogs;
