import React, { useState, useEffect } from 'react';
import { useAuth } from '../../context/AuthContext';
import { useLanguage } from '../../context/LanguageContext';
import Navbar from '../../components/Layout/Navbar';
import '../../styles/profile.css';

const Profile = () => {
  const { user, updateProfile } = useAuth();
  const { t } = useLanguage();
  
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    address: '',
    organization: '',
    emergencyContact: '',
    skills: [],
    avatar: ''
  });
  
  const [isEditing, setIsEditing] = useState(false);
  const [isLoading, setIsLoading] = useState(false);
  const [message, setMessage] = useState({ type: '', text: '' });
  const [avatarPreview, setAvatarPreview] = useState('');

  useEffect(() => {
    if (user) {
      setFormData({
        name: user.name || '',
        email: user.email || '',
        phone: user.phone || '',
        address: user.address || '',
        organization: user.organization || '',
        emergencyContact: user.emergencyContact || '',
        skills: user.skills || [],
        avatar: user.avatar || ''
      });
      setAvatarPreview(user.avatar || '');
    }
  }, [user]);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleSkillsChange = (e) => {
    const { value, checked } = e.target;
    setFormData(prev => ({
      ...prev,
      skills: checked 
        ? [...prev.skills, value]
        : prev.skills.filter(skill => skill !== value)
    }));
  };

  const handleAvatarChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      // Ê£ÄÊü•Êñá‰ª∂Â§ßÂ∞è (ÊúÄÂ§ß2MB)
      if (file.size > 2 * 1024 * 1024) {
        setMessage({ 
          type: 'error', 
          text: t('profile.avatarTooLarge') || 'Â§¥ÂÉèÊñá‰ª∂Â§ßÂ∞è‰∏çËÉΩË∂ÖËøá2MB' 
        });
        return;
      }

      // Ê£ÄÊü•Êñá‰ª∂Á±ªÂûã
      if (!file.type.startsWith('image/')) {
        setMessage({ 
          type: 'error', 
          text: t('profile.invalidImageType') || 'ËØ∑ÈÄâÊã©ÊúâÊïàÁöÑÂõæÁâáÊñá‰ª∂' 
        });
        return;
      }

      const reader = new FileReader();
      reader.onload = (e) => {
        const base64 = e.target.result;
        setAvatarPreview(base64);
        setFormData(prev => ({
          ...prev,
          avatar: base64
        }));
      };
      reader.readAsDataURL(file);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    setMessage({ type: '', text: '' });

    try {
      // ËøôÈáåÂèØ‰ª•Ë∞ÉÁî®APIÊõ¥Êñ∞Áî®Êà∑‰ø°ÊÅØ
      // ÁõÆÂâç‰ΩøÁî®Êú¨Âú∞Â≠òÂÇ®Ê®°Êãü
      const updatedUser = {
        ...user,
        ...formData,
        updatedAt: new Date().toISOString()
      };

      // Êõ¥Êñ∞localStorage‰∏≠ÁöÑÁî®Êà∑‰ø°ÊÅØ
      localStorage.setItem('user', JSON.stringify(updatedUser));
      
      // Â¶ÇÊûúÊúâupdateProfileÊñπÊ≥ïÔºåË∞ÉÁî®ÂÆÉ
      if (updateProfile) {
        await updateProfile(formData);
      }

      setMessage({ 
        type: 'success', 
        text: t('profile.updateSuccess') || '‰∏™‰∫∫‰ø°ÊÅØÊõ¥Êñ∞ÊàêÂäüÔºÅ' 
      });
      setIsEditing(false);
      
      // Âà∑Êñ∞È°µÈù¢‰ª•Êõ¥Êñ∞navbar‰∏≠ÁöÑÁî®Êà∑‰ø°ÊÅØ
      setTimeout(() => {
        window.location.reload();
      }, 1500);

    } catch (error) {
      console.error('Profile update error:', error);
      setMessage({ 
        type: 'error', 
        text: t('profile.updateError') || 'Êõ¥Êñ∞Â§±Ë¥•ÔºåËØ∑ÈáçËØï' 
      });
    } finally {
      setIsLoading(false);
    }
  };

  const handleCancel = () => {
    // ÈáçÁΩÆË°®ÂçïÊï∞ÊçÆ
    setFormData({
      name: user.name || '',
      email: user.email || '',
      phone: user.phone || '',
      address: user.address || '',
      organization: user.organization || '',
      emergencyContact: user.emergencyContact || '',
      skills: user.skills || [],
      avatar: user.avatar || ''
    });
    setAvatarPreview(user.avatar || '');
    setIsEditing(false);
    setMessage({ type: '', text: '' });
  };

  const getDefaultAvatar = () => {
    return user?.name?.charAt(0)?.toUpperCase() || 'U';
  };

  const availableSkills = [
    { value: 'medical', label: t('skills.medical') || 'ÂåªÁñóÊä§ÁêÜ' },
    { value: 'transport', label: t('skills.transport') || '‰∫§ÈÄöÂá∫Ë°å' },
    { value: 'technology', label: t('skills.technology') || 'ÊäÄÊúØÊîØÊåÅ' },
    { value: 'education', label: t('skills.education') || 'ÊïôËÇ≤ÂüπËÆ≠' },
    { value: 'household', label: t('skills.household') || 'ÂÆ∂Âä°ÊúçÂä°' },
    { value: 'companion', label: t('skills.companion') || 'Èô™‰º¥ÂÖ≥ÊÄÄ' }
  ];

  return (
    <div className="page-container">
      <Navbar userType={user?.userType} user={user} />
      
      <div className="main-content">
        <div className="profile-container">
          <div className="profile-header">
            <div className="profile-avatar-section">
              <div className="avatar-container">
                {avatarPreview ? (
                  <img 
                    src={avatarPreview} 
                    alt="Avatar" 
                    className="profile-avatar-image"
                  />
                ) : (
                  <div className="profile-avatar-placeholder">
                    {getDefaultAvatar()}
                  </div>
                )}
                {isEditing && (
                  <div className="avatar-upload">
                    <input
                      type="file"
                      id="avatar-upload"
                      accept="image/*"
                      onChange={handleAvatarChange}
                      className="avatar-input"
                    />
                    <label htmlFor="avatar-upload" className="avatar-upload-btn">
                      <span className="upload-icon">üì∑</span>
                      {t('profile.changeAvatar') || 'Êõ¥Êç¢Â§¥ÂÉè'}
                    </label>
                  </div>
                )}
              </div>
            </div>
            
            <div className="profile-info">
              <h1 className="profile-name">{user?.name || t('profile.noName')}</h1>
              <div className="profile-role">
                <span className={`role-badge role-${user?.userType}`}>
                  {user?.userType?.toUpperCase() || 'USER'}
                </span>
              </div>
              <div className="profile-actions">
                {!isEditing ? (
                  <button 
                    className="btn btn-primary"
                    onClick={() => setIsEditing(true)}
                  >
                    <span className="btn-icon">‚úèÔ∏è</span>
                    {t('profile.editProfile') || 'ÁºñËæëËµÑÊñô'}
                  </button>
                ) : (
                  <div className="edit-actions">
                    <button 
                      type="button"
                      className="btn btn-secondary"
                      onClick={handleCancel}
                      disabled={isLoading}
                    >
                      {t('common.cancel') || 'ÂèñÊ∂à'}
                    </button>
                    <button 
                      type="submit"
                      form="profile-form"
                      className="btn btn-primary"
                      disabled={isLoading}
                    >
                      {isLoading && <span className="loading-spinner"></span>}
                      {t('common.save') || '‰øùÂ≠ò'}
                    </button>
                  </div>
                )}
              </div>
            </div>
          </div>

          {message.text && (
            <div className={`alert alert-${message.type}`}>
              {message.text}
            </div>
          )}

          <div className="profile-content">
            <form id="profile-form" onSubmit={handleSubmit} className="profile-form">
              {/* Âü∫Êú¨‰ø°ÊÅØ */}
              <div className="form-section">
                <h3 className="section-title">
                  <span className="section-icon">üë§</span>
                  {t('profile.basicInfo') || 'Âü∫Êú¨‰ø°ÊÅØ'}
                </h3>
                
                <div className="form-grid">
                  <div className="form-group">
                    <label className="form-label">
                      {t('profile.name') || 'ÂßìÂêç'}
                    </label>
                    <input
                      type="text"
                      name="name"
                      value={formData.name}
                      onChange={handleInputChange}
                      className="form-input"
                      disabled={!isEditing}
                      required
                    />
                  </div>

                  <div className="form-group">
                    <label className="form-label">
                      {t('profile.email') || 'ÈÇÆÁÆ±'}
                    </label>
                    <input
                      type="email"
                      name="email"
                      value={formData.email}
                      onChange={handleInputChange}
                      className="form-input"
                      disabled={true} // ÈÇÆÁÆ±ÈÄöÂ∏∏‰∏çÂÖÅËÆ∏‰øÆÊîπ
                    />
                  </div>

                  <div className="form-group">
                    <label className="form-label">
                      {t('profile.phone') || 'ÁîµËØù'}
                    </label>
                    <input
                      type="tel"
                      name="phone"
                      value={formData.phone}
                      onChange={handleInputChange}
                      className="form-input"
                      disabled={!isEditing}
                    />
                  </div>

                  <div className="form-group form-group-full">
                    <label className="form-label">
                      {t('profile.address') || 'Âú∞ÂùÄ'}
                    </label>
                    <input
                      type="text"
                      name="address"
                      value={formData.address}
                      onChange={handleInputChange}
                      className="form-input"
                      disabled={!isEditing}
                    />
                  </div>
                </div>
              </div>

              {/* Áî®Êà∑Á±ªÂûãÁâπÂÆö‰ø°ÊÅØ */}
              {user?.userType === 'pin' && (
                <div className="form-section">
                  <h3 className="section-title">
                    <span className="section-icon">üÜò</span>
                    {t('profile.emergencyInfo') || 'Á¥ßÊÄ•ËÅîÁ≥ª‰ø°ÊÅØ'}
                  </h3>
                  
                  <div className="form-group">
                    <label className="form-label">
                      {t('profile.emergencyContact') || 'Á¥ßÊÄ•ËÅîÁ≥ª‰∫∫'}
                    </label>
                    <input
                      type="text"
                      name="emergencyContact"
                      value={formData.emergencyContact}
                      onChange={handleInputChange}
                      className="form-input"
                      disabled={!isEditing}
                      placeholder={t('profile.emergencyContactPlaceholder') || 'ÂßìÂêçÂíåÁîµËØù'}
                    />
                  </div>
                </div>
              )}

              {user?.userType === 'csr' && (
                <>
                  <div className="form-section">
                    <h3 className="section-title">
                      <span className="section-icon">üè¢</span>
                      {t('profile.workInfo') || 'Â∑•‰Ωú‰ø°ÊÅØ'}
                    </h3>
                    
                    <div className="form-group">
                      <label className="form-label">
                        {t('profile.organization') || 'ÊâÄÂ±ûÊú∫ÊûÑ'}
                      </label>
                      <input
                        type="text"
                        name="organization"
                        value={formData.organization}
                        onChange={handleInputChange}
                        className="form-input"
                        disabled={!isEditing}
                      />
                    </div>
                  </div>

                  <div className="form-section">
                    <h3 className="section-title">
                      <span className="section-icon">üõ†Ô∏è</span>
                      {t('profile.skills') || 'ÊäÄËÉΩ‰∏ìÈïø'}
                    </h3>
                    
                    <div className="skills-grid">
                      {availableSkills.map(skill => (
                        <label key={skill.value} className="skill-checkbox">
                          <input
                            type="checkbox"
                            value={skill.value}
                            checked={formData.skills.includes(skill.value)}
                            onChange={handleSkillsChange}
                            disabled={!isEditing}
                          />
                          <span className="skill-label">{skill.label}</span>
                        </label>
                      ))}
                    </div>
                  </div>
                </>
              )}

              {/* Ë¥¶Êà∑‰ø°ÊÅØ */}
              <div className="form-section">
                <h3 className="section-title">
                  <span className="section-icon">‚ÑπÔ∏è</span>
                  {t('profile.accountInfo') || 'Ë¥¶Êà∑‰ø°ÊÅØ'}
                </h3>
                
                <div className="info-grid">
                  <div className="info-item">
                    <label>{t('profile.userType') || 'Áî®Êà∑Á±ªÂûã'}:</label>
                    <span className={`user-type-badge ${user?.userType}`}>
                      {user?.userType?.toUpperCase() || 'N/A'}
                    </span>
                  </div>
                  <div className="info-item">
                    <label>{t('profile.joinDate') || 'Ê≥®ÂÜåÊó∂Èó¥'}:</label>
                    <span>
                      {user?.createdAt ? 
                        new Date(user.createdAt).toLocaleDateString('zh-CN') : 
                        'N/A'
                      }
                    </span>
                  </div>
                  <div className="info-item">
                    <label>{t('profile.lastUpdate') || 'ÊúÄÂêéÊõ¥Êñ∞'}:</label>
                    <span>
                      {user?.updatedAt ? 
                        new Date(user.updatedAt).toLocaleDateString('zh-CN') : 
                        'N/A'
                      }
                    </span>
                  </div>
                </div>
              </div>
            </form>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Profile;
