// CategoryManagement.jsx
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useLanguage } from '../../context/LanguageContext';
import { DataService } from '../../services/dataService';
import '../../styles/category-management.css';

const CategoryManagement = () => {
  const navigate = useNavigate();
  const { t } = useLanguage();
  const [categories, setCategories] = useState([]);
  const [newCategory, setNewCategory] = useState('');
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [categoryRequests, setCategoryRequests] = useState([]);
  const [showRequestModal, setShowRequestModal] = useState(false);
  const [selectedRequest, setSelectedRequest] = useState(null);
  const [showRequestDetail, setShowRequestDetail] = useState(false);
  const [showEditModal, setShowEditModal] = useState(false);
  const [editingRequest, setEditingRequest] = useState(null);

  const handleGoBack = () => {
    navigate(-1); // ËøîÂõû‰∏ä‰∏ÄÈ°µ
  };

  // Âä†ËΩΩÁ±ªÂà´Êï∞ÊçÆ
  useEffect(() => {
    loadCategories();
  }, []);

  const loadCategories = async () => {
    try {
      const allCategories = await DataService.getCategories();
      const allRequests = await DataService.getRequests();
      
      // ËÆ°ÁÆóÊØè‰∏™Á±ªÂà´ÁöÑËØ∑Ê±ÇÊï∞Èáè
      const categoriesWithCounts = (allCategories || []).map(category => ({
        ...category,
        count: (allRequests || []).filter(request => request.category === category.id).length
      }));
      
      setCategories(categoriesWithCounts);
    } catch (error) {
      console.error('Âä†ËΩΩÁ±ªÂà´Êï∞ÊçÆÂ§±Ë¥•:', error);
      setCategories([]);
    }
  };

  const handleAddCategory = async () => {
    if (newCategory.trim()) {
      const newCat = {
        id: `category_${Date.now()}`,
        name: newCategory,
        color: 'primary'
      };
      
      try {
        // ‰ΩøÁî®DataService‰øùÂ≠ò (ÁõÆÂâçÊöÇ‰∏çÂÆûÁé∞Ôºå‰ΩÜ‰øùÊåÅÊé•Âè£‰∏ÄËá¥)
        const existingCategories = await DataService.getCategories();
        const updatedCategories = [...existingCategories, newCat];
        await DataService.saveCategories(updatedCategories);
        
        setNewCategory('');
        loadCategories(); // ÈáçÊñ∞Âä†ËΩΩÊï∞ÊçÆ
      } catch (error) {
        console.error('Ê∑ªÂä†ÂàÜÁ±ªÂ§±Ë¥•:', error);
        alert('Ê∑ªÂä†ÂàÜÁ±ªÂ§±Ë¥•');
      }
    }
  };

  const handleDeleteCategory = async (id) => {
    try {
      const existingCategories = await DataService.getCategories();
      const updatedCategories = existingCategories.filter(cat => cat.id !== id);
      await DataService.saveCategories(updatedCategories);
      loadCategories(); // ÈáçÊñ∞Âä†ËΩΩÊï∞ÊçÆ
    } catch (error) {
      console.error('Âà†Èô§ÂàÜÁ±ªÂ§±Ë¥•:', error);
      alert('Âà†Èô§ÂàÜÁ±ªÂ§±Ë¥•');
    }
  };

  const handleViewCategoryRequests = async (category) => {
    try {
      // Áõ¥Êé•Ëé∑ÂèñÊâÄÊúâËØ∑Ê±ÇÁÑ∂ÂêéÊú¨Âú∞ËøáÊª§ÔºåÁ°Æ‰øùËÉΩËé∑ÂæóÁªìÊûú
      const allRequests = await DataService.getRequests();
      
      // ÂØπÊâÄÊúâËØ∑Ê±ÇËøõË°åËØ¶ÁªÜÁöÑÂàÜÁ±ªÂåπÈÖç
      const requests = (allRequests || []).filter(request => {
        // Â§öÁßçÂåπÈÖçÊñπÂºè
        return request.category === category.id || 
               request.category?.name === category.id ||
               request.category?.id === category.id ||
               (typeof request.category === 'string' && request.category === category.id);
      });
      
      setSelectedCategory(category);
      setCategoryRequests(requests || []);
      setShowRequestModal(true);
    } catch (error) {
      console.error('Ëé∑ÂèñÂàÜÁ±ªËØ∑Ê±ÇÂ§±Ë¥•:', error);
      setCategoryRequests([]);
      setShowRequestModal(true);
    }
  };

  const closeRequestModal = () => {
    setShowRequestModal(false);
    setSelectedCategory(null);
    setCategoryRequests([]);
  };

  // Êü•ÁúãËØ∑Ê±ÇËØ¶ÊÉÖ
  const handleViewRequestDetail = (request) => {
    setSelectedRequest(request);
    setShowRequestDetail(true);
  };

  // ÁºñËæëËØ∑Ê±Ç
  const handleEditRequest = (request) => {
    setEditingRequest({...request});
    setShowEditModal(true);
  };

  // ÂÜªÁªì/Ëß£ÂÜªËØ∑Ê±Ç
  const handleFreezeRequest = async (request) => {
    try {
      const newStatus = request.status === 'frozen' ? request.originalStatus || 'pending' : 'frozen';
      const updatedRequest = {
        ...request,
        status: newStatus,
        originalStatus: newStatus === 'frozen' ? request.status : undefined,
        frozenAt: newStatus === 'frozen' ? new Date().toISOString() : undefined,
        updatedAt: new Date().toISOString()
      };

      // Êõ¥Êñ∞Êú¨Âú∞Â≠òÂÇ®‰∏≠ÁöÑËØ∑Ê±Ç
      const allRequests = await DataService.getRequests();
      const updatedRequests = allRequests.map(r => 
        r.id === request.id ? updatedRequest : r
      );
      localStorage.setItem('serviceRequests', JSON.stringify(updatedRequests));

      // Êõ¥Êñ∞ÂΩìÂâçÊòæÁ§∫ÁöÑËØ∑Ê±ÇÂàóË°®
      setCategoryRequests(categoryRequests.map(r => 
        r.id === request.id ? updatedRequest : r
      ));

      // ÈáçÊñ∞Âä†ËΩΩÁ±ªÂà´Êï∞ÊçÆ‰ª•Êõ¥Êñ∞ËÆ°Êï∞
      loadCategories();
    } catch (error) {
      console.error('ÂÜªÁªì/Ëß£ÂÜªËØ∑Ê±ÇÂ§±Ë¥•:', error);
      alert('Êìç‰ΩúÂ§±Ë¥•ÔºåËØ∑ÈáçËØï');
    }
  };

  // Âà†Èô§ËØ∑Ê±Ç
  const handleDeleteRequest = async (request) => {
    if (window.confirm(`Á°ÆÂÆöË¶ÅÂà†Èô§ËØ∑Ê±Ç "${request.title}" ÂêóÔºüÊ≠§Êìç‰ΩúÊó†Ê≥ïÊí§ÈîÄ„ÄÇ`)) {
      try {
        const allRequests = await DataService.getRequests();
        const filteredRequests = allRequests.filter(r => r.id !== request.id);
        localStorage.setItem('serviceRequests', JSON.stringify(filteredRequests));

        // Êõ¥Êñ∞ÂΩìÂâçÊòæÁ§∫ÁöÑËØ∑Ê±ÇÂàóË°®
        setCategoryRequests(categoryRequests.filter(r => r.id !== request.id));

        // ÈáçÊñ∞Âä†ËΩΩÁ±ªÂà´Êï∞ÊçÆ‰ª•Êõ¥Êñ∞ËÆ°Êï∞
        loadCategories();
      } catch (error) {
        console.error('Âà†Èô§ËØ∑Ê±ÇÂ§±Ë¥•:', error);
        alert('Âà†Èô§Â§±Ë¥•ÔºåËØ∑ÈáçËØï');
      }
    }
  };

  // ‰øùÂ≠òÁºñËæëÁöÑËØ∑Ê±Ç
  const handleSaveEditedRequest = async () => {
    if (!editingRequest) return;

    try {
      const updatedRequest = {
        ...editingRequest,
        updatedAt: new Date().toISOString()
      };

      const allRequests = await DataService.getRequests();
      const updatedRequests = allRequests.map(r => 
        r.id === editingRequest.id ? updatedRequest : r
      );
      localStorage.setItem('serviceRequests', JSON.stringify(updatedRequests));

      // Êõ¥Êñ∞ÂΩìÂâçÊòæÁ§∫ÁöÑËØ∑Ê±ÇÂàóË°®
      setCategoryRequests(categoryRequests.map(r => 
        r.id === editingRequest.id ? updatedRequest : r
      ));

      setShowEditModal(false);
      setEditingRequest(null);
      loadCategories();
    } catch (error) {
      console.error('‰øùÂ≠òËØ∑Ê±ÇÂ§±Ë¥•:', error);
      alert('‰øùÂ≠òÂ§±Ë¥•ÔºåËØ∑ÈáçËØï');
    }
  };

  // ÂÖ≥Èó≠ËØ¶ÊÉÖÊ®°ÊÄÅÊ°Ü
  const closeRequestDetail = () => {
    setShowRequestDetail(false);
    setSelectedRequest(null);
  };

  // ÂÖ≥Èó≠ÁºñËæëÊ®°ÊÄÅÊ°Ü
  const closeEditModal = () => {
    setShowEditModal(false);
    setEditingRequest(null);
  };

  return (
    <div className="admin-page">
      <div className="page-header">
        <div className="page-nav">
          <button 
            className="btn btn-back"
            onClick={handleGoBack}
            title={t('common.back') || 'ËøîÂõû'}
          >
            <span className="btn-icon">‚Üê</span>
            {t('common.back') || 'ËøîÂõû'}
          </button>
        </div>
        <h1 className="page-title">{t('admin.categories.title')}</h1>
        <p className="page-subtitle">{t('admin.categories.subtitle')}</p>
      </div>

      <div className="card">
        <div className="card-header">
          <h3 className="card-title">{t('admin.categories.addNew')}</h3>
        </div>
        <div className="card-body">
          <div className="input-group">
            <input 
              type="text" 
              className="form-input"
              placeholder={t('admin.categories.placeholder')}
              value={newCategory}
              onChange={(e) => setNewCategory(e.target.value)}
              onKeyPress={(e) => e.key === 'Enter' && handleAddCategory()}
            />
            <button 
              className="btn btn-primary"
              onClick={handleAddCategory}
              disabled={!newCategory.trim()}
            >
              <span className="btn-icon">+</span>
              {t('admin.categories.add')}
            </button>
          </div>
        </div>
      </div>

      <div className="card">
        <div className="card-header">
          <h3 className="card-title">{t('admin.categories.existing')}</h3>
          <span className="badge">{categories.length} {t('admin.categories.count')}</span>
        </div>
        <div className="card-body">
          <div className="category-list">
            {categories.map(category => (
              <div 
                key={category.id} 
                className="category-item"
                onClick={() => handleViewCategoryRequests(category)}
                title={category.count > 0 ? (t('admin.categories.clickToView') || 'ÁÇπÂáªÊü•ÁúãÁõ∏ÂÖ≥ËØ∑Ê±Ç') : ''}
              >
                <div className="category-info">
                  <div className="category-icon">üìÅ</div>
                  <div className="category-details">
                    <h4 className="category-name">{category.name}</h4>
                    <p className="category-count">
                      {category.count} {t('admin.categories.relatedRequests')}
                      {category.count > 0 && (
                        <span className="click-hint"> ‚Ä¢ {t('admin.categories.clickHint') || 'ÁÇπÂáªÊü•Áúã'}</span>
                      )}
                    </p>
                  </div>
                </div>
                <div className="category-actions">
                  <button 
                    className="btn btn-danger btn-sm delete-btn"
                    onClick={(e) => {
                      e.stopPropagation();
                      handleDeleteCategory(category.id);
                    }}
                    title={t('common.delete') || 'Âà†Èô§'}
                  >
                    üóëÔ∏è
                  </button>
                </div>
              </div>
            ))}
          </div>
        </div>
      </div>

      {/* ËØ∑Ê±ÇÂàóË°®Ê®°ÊÄÅÊ°Ü */}
      {showRequestModal && selectedCategory && (
        <div className="modal-overlay" onClick={closeRequestModal}>
          <div className="modal-content large" onClick={(e) => e.stopPropagation()}>
            <div className="modal-header">
              <h3 className="modal-title">
                {selectedCategory.name} - {t('admin.categories.requestList') || 'ËØ∑Ê±ÇÂàóË°®'}
              </h3>
              <button className="modal-close" onClick={closeRequestModal}>√ó</button>
            </div>
            <div className="modal-body">
              <div className="request-stats">
                <div className="stat-item">
                  <span className="stat-label">{t('admin.categories.totalRequests') || 'ÊÄªËØ∑Ê±ÇÊï∞'}:</span>
                  <span className="stat-value">{categoryRequests.length}</span>
                </div>
                <div className="stat-item">
                  <span className="stat-label">{t('admin.categories.pendingRequests') || 'ÂæÖÂ§ÑÁêÜ'}:</span>
                  <span className="stat-value">
                    {categoryRequests.filter(r => r.status === 'pending').length}
                  </span>
                </div>
                <div className="stat-item">
                  <span className="stat-label">{t('admin.categories.matchedRequests') || 'Â∑≤ÂåπÈÖç'}:</span>
                  <span className="stat-value">
                    {categoryRequests.filter(r => r.status === 'matched').length}
                  </span>
                </div>
              </div>
              {categoryRequests.length > 0 ? (
                <div className="requests-list">
                  {categoryRequests.map(request => (
                    <div key={request.id} className="request-item">
                      <div className="request-header">
                        <h4 className="request-title">{request.title}</h4>
                        <span className={`status-badge ${request.status}`}>
                          {t(DataService.getStatusById(request.status).name)}
                        </span>
                      </div>
                      <div className="request-details">
                        <p className="request-description">{request.description}</p>
                        <div className="request-meta">
                          <span className="meta-item">
                            <strong>{t('common.urgency') || 'Á¥ßÊÄ•Á®ãÂ∫¶'}:</strong> 
                            {t(DataService.getUrgencyById(request.urgency).name)}
                          </span>
                          <span className="meta-item">
                            <strong>{t('common.location') || 'Âú∞ÁÇπ'}:</strong> 
                            {request.location?.address || request.location || 'ÂæÖÁ°ÆÂÆö'}
                          </span>
                          <span className="meta-item">
                            <strong>{t('common.createdAt') || 'ÂàõÂª∫Êó∂Èó¥'}:</strong> 
                            {DataService.getTimeAgo(request.createdAt, t)}
                          </span>
                        </div>
                        {request.volunteer && (
                          <div className="volunteer-info">
                            <strong>{t('common.volunteer') || 'ÂøóÊÑøËÄÖ'}:</strong> {request.volunteer}
                          </div>
                        )}
                      </div>
                    </div>
                  ))}
                </div>
              ) : (
                <div className="empty-state">
                  <div className="empty-icon">üì≠</div>
                  <h3>{t('admin.categories.noRequests') || 'ÊöÇÊó†ËØ∑Ê±Ç'}</h3>
                  <p>{t('admin.categories.noRequestsDesc') || 'ËØ•Á±ªÂà´‰∏ãËøòÊ≤°Êúâ‰ªª‰ΩïËØ∑Ê±Ç'}</p>
                </div>
              )}
            </div>
            <div className="modal-footer">
              <button className="btn btn-secondary" onClick={closeRequestModal}>
                {t('common.close') || 'ÂÖ≥Èó≠'}
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default CategoryManagement;
