// ËØ∑Ê±ÇËØ¶ÊÉÖÊ®°ÊÄÅÊ°ÜÁªÑ‰ª∂
import React from 'react';

const RequestDetailModal = ({ request, onClose }) => {
  if (!request) return null;

  const getCategoryName = (category) => {
    const categories = {
      medical: 'ÂåªÁñóÈô™Âêå',
      transportation: '‰∫§ÈÄöÂ∏ÆÂä©',
      shopping: 'Ë¥≠Áâ©ÂçèÂä©',
      household: 'ÂÆ∂Âä°Â∏ÆÂä©',
      companion: 'Èô™‰º¥ÊúçÂä°',
      technology: 'ÊäÄÊúØÊîØÊåÅ',
      other: 'ÂÖ∂‰ªñ'
    };
    return categories[category] || category;
  };

  const getUrgencyName = (urgency) => {
    const urgencyMap = {
      low: '‰Ωé',
      medium: '‰∏≠',
      high: 'È´ò',
      urgent: 'Á¥ßÊÄ•'
    };
    return urgencyMap[urgency] || urgency;
  };

  const formatDateTime = (dateString, timeString) => {
    if (dateString) {
      const date = new Date(dateString);
      const formattedDate = date.toLocaleDateString('zh-CN');
      
      // Â§ÑÁêÜÊó∂Èó¥ÊÆµ
      let timeDisplay = '';
      if (timeString) {
        const timeMap = {
          'morning': '‰∏äÂçà (9:00-12:00)',
          'afternoon': '‰∏ãÂçà (12:00-18:00)', 
          'evening': 'Êôö‰∏ä (18:00-21:00)'
        };
        timeDisplay = timeMap[timeString] || timeString;
        return `${formattedDate} ${timeDisplay}`;
      }
      
      return formattedDate;
    }
    return 'ÂæÖÂÆö';
  };

  return (
    <div className="modal-backdrop">
      <div className="modal modal-lg">
        <div className="modal-header">
          <h3 className="modal-title">ËØ∑Ê±ÇËØ¶ÊÉÖ</h3>
          <button className="modal-close" onClick={onClose} type="button">
            ‚úï
          </button>
        </div>
        
        <div className="modal-body">
          <div className="request-detail">
            {/* Âü∫Êú¨‰ø°ÊÅØ */}
            <div className="detail-section">
              <h4 className="detail-section-title">Âü∫Êú¨‰ø°ÊÅØ</h4>
              <div className="detail-grid">
                <div className="detail-item">
                  <label className="detail-label">ËØ∑Ê±ÇÊ†áÈ¢ò</label>
                  <div className="detail-value">{request.title}</div>
                </div>
                <div className="detail-item">
                  <label className="detail-label">ÊúçÂä°Á±ªÂûã</label>
                  <div className="detail-value">
                    <span className="category-tag">{getCategoryName(request.category)}</span>
                  </div>
