// CategoryManagement.jsx
import React, { useState } from 'react';

const CategoryManagement = () => {
  const [categories, setCategories] = useState([
    { id: 1, name: 'ÂåªÁñóÈô™Âêå', count: 12 },
    { id: 2, name: '‰∫§ÈÄöÂ∏ÆÂä©', count: 8 },
    { id: 3, name: 'Ë¥≠Áâ©ÂçèÂä©', count: 15 },
    { id: 4, name: 'ÂÆ∂Âä°Â∏ÆÂä©', count: 6 }
  ]);
  const [newCategory, setNewCategory] = useState('');

  const handleAddCategory = () => {
    if (newCategory.trim()) {
      const newCat = {
        id: Date.now(),
        name: newCategory,
        count: 0
      };
      setCategories([...categories, newCat]);
      setNewCategory('');
    }
  };

  const handleDeleteCategory = (id) => {
    setCategories(categories.filter(cat => cat.id !== id));
  };

  return (
    <div className="admin-page">
      <div className="page-header">
        <h1 className="page-title">ÊúçÂä°Á±ªÂà´ÁÆ°ÁêÜ</h1>
        <p className="page-subtitle">ÁÆ°ÁêÜÁ≥ªÁªü‰∏≠ÁöÑÊúçÂä°Á±ªÂà´</p>
      </div>

      <div className="card">
        <div className="card-header">
          <h3 className="card-title">Ê∑ªÂä†Êñ∞Á±ªÂà´</h3>
        </div>
        <div className="card-body">
          <div className="input-group">
            <input 
              type="text" 
              className="form-input"
              placeholder="ËæìÂÖ•Êñ∞Á±ªÂà´ÂêçÁß∞..."
              value={newCategory}
              onChange={(e) => setNewCategory(e.target.value)}
              onKeyPress={(e) => e.key === 'Enter' && handleAddCategory()}
            />
            <button 
              className="btn btn-primary"
              onClick={handleAddCategory}
              disabled={!newCategory.trim()}
            >
              <span className="btn-icon">+</span>
              Ê∑ªÂä†Á±ªÂà´
            </button>
          </div>
        </div>
      </div>

      <div className="card">
        <div className="card-header">
          <h3 className="card-title">Áé∞ÊúâÁ±ªÂà´</h3>
          <span className="badge">{categories.length} ‰∏™Á±ªÂà´</span>
        </div>
        <div className="card-body">
          <div className="grid-list">
            {categories.map(category => (
              <div key={category.id} className="grid-item">
                <div className="item-content">
                  <div className="item-icon">üìÅ</div>
                  <div className="item-info">
                    <h4 className="item-title">{category.name}</h4>
                    <p className="item-subtitle">{category.count} ‰∏™Áõ∏ÂÖ≥ËØ∑Ê±Ç</p>
                  </div>
                </div>
                <button 
                  className="btn btn-danger btn-sm"
                  onClick={() => handleDeleteCategory(category.id)}
                >
                  Âà†Èô§
                </button>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default CategoryManagement;
