// System Alerts - Á≥ªÁªüË≠¶Êä•ÈÖçÁΩÆÈ°µÈù¢ (System Admin Only)
import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import { useLanguage } from '../../context/LanguageContext';
import Navbar from '../../components/Layout/Navbar';
import { isSystemAdmin } from '../../utils/permissions';
import '../../styles/user-management.css';

const SystemAlerts = () => {
  const navigate = useNavigate();
  const { user } = useAuth();
  const { t } = useLanguage();
  const [alerts, setAlerts] = useState([]);
  const [showCreateModal, setShowCreateModal] = useState(false);
  const [editingAlert, setEditingAlert] = useState(null);

  // ÊùÉÈôêÊ£ÄÊü• - Âè™Êúâ System Admin ÂèØ‰ª•ËÆøÈóÆ
  useEffect(() => {
    if (!isSystemAdmin(user)) {
      alert(t('permission.denied') || 'Access Denied: System Admin only');
      navigate('/admin/dashboard');
    }
  }, [user, navigate, t]);

  // ÂàùÂßãÂåñË≠¶Êä•ÈÖçÁΩÆ
  useEffect(() => {
    loadAlerts();
  }, []);

  const loadAlerts = () => {
    // Ê®°ÊãüË≠¶Êä•ÈÖçÁΩÆÊï∞ÊçÆ
    const mockAlerts = [
      {
        id: 1,
        name: t('admin.alerts.failedLoginAlert') || 'Â§±Ë¥•ÁôªÂΩïË≠¶Êä•',
        type: 'security',
        condition: 'failed_login_attempts',
        threshold: 5,
        enabled: true,
        recipients: ['admin@system.com', 'security@system.com'],
        description: t('admin.alerts.failedLoginDesc') || 'ÂΩìÁî®Êà∑ËøûÁª≠Â§±Ë¥•ÁôªÂΩïË∂ÖËøáÈòàÂÄºÊó∂Ëß¶Âèë',
        lastTriggered: new Date(Date.now() - 1000 * 60 * 60 * 2).toISOString(),
        triggerCount: 3
      },
      {
        id: 2,
        name: t('admin.alerts.systemResourceAlert') || 'Á≥ªÁªüËµÑÊ∫êË≠¶Êä•',
        type: 'system',
        condition: 'high_memory_usage',
        threshold: 85,
        enabled: true,
        recipients: ['admin@system.com', 'ops@system.com'],
        description: t('admin.alerts.systemResourceDesc') || 'ÂΩìÁ≥ªÁªüÂÜÖÂ≠ò‰ΩøÁî®ÁéáË∂ÖËøá85%Êó∂Ëß¶Âèë',
        lastTriggered: null,
        triggerCount: 0
      },
      {
        id: 3,
        name: t('admin.alerts.userSurgeAlert') || 'Áî®Êà∑Ê≥®ÂÜåÊøÄÂ¢û',
        type: 'user',
        condition: 'high_registration_rate',
        threshold: 50,
        enabled: false,
        recipients: ['admin@system.com'],
        description: t('admin.alerts.userSurgeDesc') || 'ÂΩìÊØèÂ∞èÊó∂Ê≥®ÂÜåÁî®Êà∑Ë∂ÖËøá50‰∫∫Êó∂Ëß¶Âèë',
        lastTriggered: null,
        triggerCount: 0
      },
      {
        id: 4,
        name: t('admin.alerts.dbConnectionAlert') || 'Êï∞ÊçÆÂ∫ìËøûÊé•Â§±Ë¥•',
        type: 'system',
        condition: 'database_connection_failed',
        threshold: 1,
        enabled: true,
        recipients: ['admin@system.com', 'ops@system.com', 'tech@system.com'],
        description: t('admin.alerts.dbConnectionDesc') || 'Êï∞ÊçÆÂ∫ìËøûÊé•Â§±Ë¥•Êó∂Á´ãÂç≥Ëß¶Âèë',
        lastTriggered: null,
        triggerCount: 0
      },
      {
        id: 5,
        name: t('admin.alerts.suspiciousIPAlert') || 'ÂèØÁñëIPÊ¥ªÂä®',
        type: 'security',
        condition: 'suspicious_ip_activity',
        threshold: 10,
        enabled: true,
        recipients: ['security@system.com', 'admin@system.com'],
        description: t('admin.alerts.suspiciousIPDesc') || 'ÂΩìÂêå‰∏ÄIPÂú®Áü≠Êó∂Èó¥ÂÜÖÊúâÂºÇÂ∏∏ËØ∑Ê±ÇÊó∂Ëß¶Âèë',
        lastTriggered: new Date(Date.now() - 1000 * 60 * 60 * 24).toISOString(),
        triggerCount: 1
      }
    ];

    setAlerts(mockAlerts);
  };

  const handleToggleAlert = (alertId) => {
    setAlerts(alerts.map(alert =>
      alert.id === alertId ? { ...alert, enabled: !alert.enabled } : alert
    ));
  };

  const handleDeleteAlert = (alertId) => {
    if (window.confirm(t('admin.alerts.confirmDelete') || 'Á°ÆËÆ§Âà†Èô§Ê≠§Ë≠¶Êä•ÈÖçÁΩÆÔºü')) {
      setAlerts(alerts.filter(alert => alert.id !== alertId));
    }
  };

  const getTypeColor = (type) => {
    const colors = {
      security: 'status-suspended',
      system: 'status-active',
      user: 'user-type-badge primary'
    };
    return colors[type] || 'user-type-badge';
  };

  const getTypeIcon = (type) => {
    const icons = {
      security: 'üîí',
      system: '‚öôÔ∏è',
      user: 'üë•'
    };
    return icons[type] || 'üìã';
  };

  const formatTimestamp = (timestamp) => {
    if (!timestamp) return t('common.never') || '‰ªéÊú™';
    const date = new Date(timestamp);
    const locale = t('common.locale') || 'zh-CN';
    return date.toLocaleString(locale, {
      year: 'numeric',
      month: '2-digit',
      day: '2-digit',
