// Login.jsx
import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import { useLanguage } from '../../context/LanguageContext';
import LanguageSwitcher from '../../components/Common/LanguageSwitcher';

const Login = () => {
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    userType: ''
  });
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  
  const { login } = useAuth();
  const { t } = useLanguage();
  const navigate = useNavigate();
  
  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setIsLoading(true);
    
    // Ê£ÄÊü•ÊòØÂê¶ÊòØÁÆ°ÁêÜÂëòÈÇÆÁÆ±
    const isAdmin = formData.email === 'admin@test.com' || formData.email.endsWith('@admin.com');
    
    if (!formData.userType && !isAdmin) {
      setError(t('auth.login.error.userType'));
      setIsLoading(false);
      return;
    }
    
    try {
      // Ë∞ÉÁî®AuthContextÁöÑloginÊñπÊ≥ï
      const result = await login(formData.email, formData.password, formData.userType);
      
      if (result.success) {
        // ÁôªÂΩïÊàêÂäüÔºåÂØºËà™Âà∞Áõ∏Â∫îÈ°µÈù¢
        const userType = isAdmin ? 'admin' : formData.userType;
        switch (userType) {
          case 'pin':
            navigate('/pin/dashboard');
            break;
          case 'csr':
            navigate('/csr/search');
            break;
          case 'admin':
            navigate('/admin/dashboard');
            break;
          default:
            navigate('/');
        }
      } else {
        setError(result.error || t('common.error'));
      }
    } catch (error) {
      setError(t('common.error'));
      console.error('ÁôªÂΩïÈîôËØØ:', error);
    } finally {
      setIsLoading(false);
    }
  };
  
  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  // Ê£ÄÊü•ÂΩìÂâçÈÇÆÁÆ±ÊòØÂê¶ÊòØÁÆ°ÁêÜÂëòÈÇÆÁÆ±
  const isAdminEmail = formData.email === 'admin@test.com' || formData.email.endsWith('@admin.com');

  const userTypes = [
    { value: 'pin', label: t('userType.pin'), icon: 'üôã', description: t('userType.pin.description') },
    { value: 'csr', label: t('userType.csr'), icon: 'ü§ù', description: t('userType.csr.description') }
  ];
  
  return (
    <div className="auth-container">
      <div className="auth-background">
        <div className="auth-hero">
          <div className="hero-icon">ü§ù</div>
          <h1 className="hero-title">{t('app.title')}</h1>
          <p className="hero-subtitle">{t('app.subtitle')}</p>
        </div>
      </div>
      
      <div className="auth-card">
        <div className="auth-header">
          <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '1rem' }}>
            <div>
              <h2 className="auth-title">{t('auth.login.title')}</h2>
              <p className="auth-subtitle">{t('auth.login.subtitle')}</p>
            </div>
            <LanguageSwitcher />
          </div>
        </div>
        
        {error && (
          <div className="alert alert-error">
            <span className="alert-icon">‚ö†Ô∏è</span>
            {error}
          </div>
        )}
        
        <form className="auth-form" onSubmit={handleSubmit}>
          <div className="form-group">
            <label className="form-label">{t('auth.login.userType')}</label>
            <div className="user-type-grid">
              {userTypes.map(type => (
                <label 
                  key={type.value}
                  className={`user-type-card ${
                    formData.userType === type.value ? 'selected' : ''
                  }`}
                >
                  <input
                    type="radio"
                    name="userType"
                    value={type.value}
                    checked={formData.userType === type.value}
                    onChange={handleChange}
                    className="sr-only"
                  />
                  <div className="user-type-icon">{type.icon}</div>
                  <div className="user-type-info">
                    <div className="user-type-label">{type.label}</div>
                    <div className="user-type-desc">{type.description}</div>
                  </div>
                </label>
              ))}
            </div>
          </div>
          
          <div className="form-group">
            <label className="form-label">{t('auth.login.email')}</label>
            <div className="input-with-icon">
              <span className="input-icon">üìß</span>
              <input 
                type="email" 
                name="email"
                className="form-input"
                placeholder={t('auth.login.email')}
                value={formData.email}
                onChange={handleChange}
                required
              />
            </div>
          </div>
          
          <div className="form-group">
            <label className="form-label">{t('auth.login.password')}</label>
            <div className="input-with-icon">
              <span className="input-icon">üîí</span>
              <input 
                type="password" 
                name="password"
                className="form-input"
                placeholder={t('auth.login.password')}
                value={formData.password}
                onChange={handleChange}
                required
              />
            </div>
          </div>
          
          <button 
            type="submit" 
            className={`btn btn-primary btn-full ${isLoading ? 'loading' : ''}`}
            disabled={isLoading}
          >
            {isLoading ? (
              <>
                <span className="loading-spinner"></span>
                {t('common.loading')}
              </>
            ) : (
              t('auth.login.submit')
            )}
          </button>
        </form>
        
        <div className="auth-footer">
          <p className="auth-link">
            {t('auth.login.noAccount')} <Link to="/register" className="link">{t('auth.login.registerNow')}</Link>
          </p>
          <div className="demo-accounts">
            <p className="demo-title">{t('auth.login.demoAccounts')}</p>
            <div className="demo-list">
              <span>pin@test.com</span>
              <span>csr@test.com</span>
              <span>admin@test.com</span>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
