// Dashboard_new.jsx
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import { useLanguage } from '../../context/LanguageContext';
import { DataService } from '../../services/dataService';
import Navbar from '../../components/Layout/Navbar';

const AdminDashboard = () => {
  const { user } = useAuth();
  const { t } = useLanguage();
  const [dashboardData, setDashboardData] = useState({
    stats: [],
    activities: []
  });
  const [loading, setLoading] = useState(true);
  const [lastUpdated, setLastUpdated] = useState(null);
  
  useEffect(() => {
    // ÂàùÂßãÂåñÁ§∫‰æãÊï∞ÊçÆÔºàÂ¶ÇÊûúÈúÄË¶ÅÔºâ
    initializeSampleData();
    
    // Âä†ËΩΩ‰ª™Ë°®ÊùøÊï∞ÊçÆ
    const loadDashboardData = () => {
      try {
        const data = AdminDashboardService.getDashboardData();
        setDashboardData(data);
        setLastUpdated(new Date());
      } catch (error) {
        console.error('Âä†ËΩΩ‰ª™Ë°®ÊùøÊï∞ÊçÆÂ§±Ë¥•:', error);
        // ‰ΩøÁî®ÈªòËÆ§Êï∞ÊçÆ‰Ωú‰∏∫ÂêéÂ§á
        setDashboardData({
          stats: [
            { title: 'ÊÄªÁî®Êà∑Êï∞', value: '0', change: '+0', trend: 'neutral', icon: 'üë•', color: 'primary' },
            { title: 'Ê¥ªË∑ÉËØ∑Ê±Ç', value: '0', change: '+0', trend: 'neutral', icon: 'üìã', color: 'secondary' },
            { title: '‰ªäÊó•ÂåπÈÖç', value: '0', change: '+0', trend: 'neutral', icon: '‚úÖ', color: 'success' },
            { title: 'ÂæÖÂÆ°Ê†∏', value: '0', change: '+0', trend: 'neutral', icon: '‚è≥', color: 'warning' }
          ],
          activities: []
        });
      } finally {
        setLoading(false);
      }
    };
    
    loadDashboardData();
    
    // ÂèØÈÄâÔºöËÆæÁΩÆÂÆöÊó∂Âà∑Êñ∞
    const interval = setInterval(loadDashboardData, 30000); // ÊØè30ÁßíÂà∑Êñ∞‰∏ÄÊ¨°
    
    return () => clearInterval(interval);
  }, []);

  const quickActions = [
    { title: 'Áî®Êà∑ÁÆ°ÁêÜ', icon: 'üë•', path: '/admin/users', color: 'blue' },
    { title: 'ÊúçÂä°Á±ªÂà´', icon: 'üìÅ', path: '/admin/categories', color: 'green' },
    { title: 'Êï∞ÊçÆÊä•Âëä', icon: 'üìä', path: '/admin/reports', color: 'purple' },
    { title: 'Á≥ªÁªüËÆæÁΩÆ', icon: '‚öôÔ∏è', path: '/admin/settings', color: 'gray' }
  ];

  return (
    <div className="page-container">
      <Navbar userType="admin" user={user} />
      <div className="main-content">
        <div className="dashboard">
          <div className="dashboard-header">
            <div className="header-content">
              <h1 className="dashboard-title">ÁÆ°ÁêÜÂëò‰ª™Ë°®Êùø</h1>
              <p className="dashboard-subtitle">Ê¨¢ËøéÂõûÊù•Ôºå{user?.name}ÔºÅËøôÈáåÊòØÁ≥ªÁªüÊ¶ÇËßà</p>
              {lastUpdated && (
                <div className="data-fresh-indicator fresh">
                  <span>üîÑ</span>
                  <span>Êï∞ÊçÆÊõ¥Êñ∞‰∫é: {lastUpdated.toLocaleTimeString('zh-CN')}</span>
                </div>
              )}
            </div>
            <div className="header-actions">
              <button 
                className="btn btn-secondary"
                onClick={() => {
                  setLoading(true);
                  setTimeout(() => {
                    const data = AdminDashboardService.getDashboardData();
                    setDashboardData(data);
                    setLastUpdated(new Date());
                    setLoading(false);
