// CSRæœç´¢é¡µé¢
import React, { useState, useEffect } from 'react';
import { useAuth } from '../../context/AuthContext';
import { useLanguage } from '../../context/LanguageContext';
import { DataService } from '../../services/dataService';
import Navbar from '../../components/Layout/Navbar';
import RequestDetailModal from '../../components/PIN/RequestDetailModal';
import '../../styles/search.css';

const CSRSearch = () => {
  const { user } = useAuth();
  const { t } = useLanguage();
  const [requests, setRequests] = useState([]);
  const [filteredRequests, setFilteredRequests] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('');
  const [selectedUrgency, setSelectedUrgency] = useState('');
  const [showRequestDetail, setShowRequestDetail] = useState(null);
  const [shortlistedRequests, setShortlistedRequests] = useState([]);

  // è°ƒè¯•ï¼šåœ¨ç»„ä»¶æ¸²æŸ“æ—¶æ‰“å°ç”¨æˆ·ä¿¡æ¯
  console.log('CSRæœç´¢é¡µé¢ - å½“å‰ç”¨æˆ·:', user);

  // åŠ è½½æ‰€æœ‰è¯·æ±‚å’Œæ”¶è—å¤¹
  useEffect(() => {
    // åˆå§‹åŒ–æ•°æ®
    DataService.initializeData();
    
    // è·å–æ‰€æœ‰å¾…å¤„ç†çš„è¯·æ±‚
    const allRequests = DataService.getRequests().filter(request => request.status === 'pending');
    setRequests(allRequests);
    setFilteredRequests(allRequests);
    
    // åŠ è½½ç”¨æˆ·çš„æ”¶è—å¤¹
    if (user?.id) {
      const userShortlists = DataService.getUserShortlists(user.id);
      setShortlistedRequests(userShortlists);
    }
  }, [user?.id]);

  // æœç´¢å’Œè¿‡æ»¤é€»è¾‘
  useEffect(() => {
    let filtered = requests;

    // æŒ‰æœç´¢è¯è¿‡æ»¤
    if (searchTerm) {
      filtered = filtered.filter(request => 
        request.title.toLowerCase().includes(searchTerm.toLowerCase()) ||
        request.description.toLowerCase().includes(searchTerm.toLowerCase()) ||
        request.location.toLowerCase().includes(searchTerm.toLowerCase())
      );
    }

    // æŒ‰åˆ†ç±»è¿‡æ»¤
    if (selectedCategory) {
      filtered = filtered.filter(request => request.category === selectedCategory);
    }

    // æŒ‰ç´§æ€¥ç¨‹åº¦è¿‡æ»¤
    if (selectedUrgency) {
      filtered = filtered.filter(request => request.urgency === selectedUrgency);
    }

    setFilteredRequests(filtered);
  }, [requests, searchTerm, selectedCategory, selectedUrgency]);

  // å¤„ç†æŸ¥çœ‹è¯·æ±‚è¯¦æƒ…
  const handleViewDetail = (request) => {
    setShowRequestDetail(request);
  };

  // å¤„ç†æ·»åŠ /ç§»é™¤æ”¶è—
  const handleToggleShortlist = (request) => {
    if (!user || !user.id) {
      alert(t('csr.search.loginRequired'));
      return;
    }

    const isShortlisted = shortlistedRequests.some(req => req.id === request.id);
    let updatedShortlist;

    if (isShortlisted) {
      // ç§»é™¤æ”¶è—
      updatedShortlist = shortlistedRequests.filter(req => req.id !== request.id);
      alert(t('csr.search.removeFromShortlist'));
    } else {
      // æ·»åŠ æ”¶è—
      updatedShortlist = [...shortlistedRequests, { ...request, shortlistedAt: new Date().toISOString() }];
      alert(t('csr.search.addToShortlist'));
    }

    setShortlistedRequests(updatedShortlist);
    localStorage.setItem(`shortlist_${user.id}`, JSON.stringify(updatedShortlist));
  };

  // æ£€æŸ¥è¯·æ±‚æ˜¯å¦å·²æ”¶è—
  const isRequestShortlisted = (requestId) => {
    return shortlistedRequests.some(req => req.id === requestId);
  };

  // å¤„ç†ç”³è¯·å¿—æ„¿æœåŠ¡
  const handleApplyVolunteer = (requestId) => {
    // è°ƒè¯•ä¿¡æ¯
    console.log('ç”¨æˆ·å¯¹è±¡:', user);
    console.log('ç”¨æˆ·ID:', user?.id);
    console.log('ç”¨æˆ·é‚®ç®±:', user?.email);
    
    if (!user || !user.email) {
      alert(t('csr.search.loginRequired'));
      return;
    }

    if (window.confirm(t('csr.search.confirmApply'))) {
      try {
        // æ›´æ–°è¯·æ±‚çŠ¶æ€ä¸ºå·²åŒ¹é…
        const allRequests = JSON.parse(localStorage.getItem('userRequests') || '[]');
        const updatedRequests = allRequests.map(req => 
          req.id === requestId 
            ? { 
                ...req, 
                status: 'matched', 
                volunteer: user.name || user.email || 'CSRå¿—æ„¿è€…', 
                volunteerId: user.id,
                matchedAt: new Date().toISOString() 
              }
            : req
        );
        localStorage.setItem('userRequests', JSON.stringify(updatedRequests));
        
        // æ›´æ–°æœ¬åœ°çŠ¶æ€
        setRequests(prev => prev.filter(req => req.id !== requestId));
        setFilteredRequests(prev => prev.filter(req => req.id !== requestId));
        
        alert(t('csr.search.applySuccess'));
      } catch (error) {
        console.error('ç”³è¯·å¿—æ„¿æœåŠ¡æ—¶å‡ºé”™:', error);
        alert(t('csr.search.applyError'));
      }
    }
  };
  
  return (
    <div className="page-container">
      <Navbar userType="csr" user={user} />
      <div className="main-content">
        <div className="dashboard">
          <div className="dashboard-header">
            <h1 className="dashboard-title">{t('csr.search.title')}</h1>
            <p className="dashboard-subtitle">{t('csr.search.subtitle')}</p>
            
            {/* ä¸´æ—¶è°ƒè¯•ä¿¡æ¯ */}
            {user && (
              <div style={{ 
                background: '#e3f2fd', 
                padding: '0.5rem', 
                borderRadius: '0.25rem', 
                fontSize: '0.8rem',
                marginTop: '0.5rem',
                color: '#1565c0'
              }}>
                <strong>{t('debug.currentUser')}:</strong> {user.name || user.email} ({user.userType}) - ID: {user.id}
              </div>
            )}
            {!user && (
              <div style={{ 
                background: '#ffebee', 
                padding: '0.5rem', 
                borderRadius: '0.25rem', 
                fontSize: '0.8rem',
                marginTop: '0.5rem',
                color: '#c62828'
              }}>
                <strong>{t('debug.noUser')}</strong>
              </div>
            )}
          </div>

          {/* æœç´¢å’Œè¿‡æ»¤å™¨ */}
          <div className="card" style={{ marginBottom: '2rem' }}>
            <div className="card-body">
              <div className="search-filters">
                <div className="search-bar">
                  <input
                    type="text"
                    placeholder="æœç´¢è¯·æ±‚æ ‡é¢˜ã€æè¿°æˆ–åœ°ç‚¹..."
                    value={searchTerm}
                    onChange={(e) => setSearchTerm(e.target.value)}
                    className="form-input"
                    style={{ flex: 1 }}
                  />
                </div>
                
                <div className="filter-row" style={{ display: 'flex', gap: '1rem', marginTop: '1rem' }}>
                  <select
                    value={selectedCategory}
                    onChange={(e) => setSelectedCategory(e.target.value)}
                    className="form-select"
                  >
                    <option value="">æ‰€æœ‰åˆ†ç±»</option>
                    <option value="medical">åŒ»ç–—é™ªåŒ</option>
                    <option value="transport">äº¤é€šå¸®åŠ©</option>
                    <option value="shopping">è´­ç‰©ååŠ©</option>
                    <option value="household">å®¶åŠ¡å¸®åŠ©</option>
                    <option value="companion">é™ªä¼´æœåŠ¡</option>
                    <option value="technology">æŠ€æœ¯æ”¯æŒ</option>
                    <option value="education">æ•™è‚²è¾…å¯¼</option>
                    <option value="other">å…¶ä»–</option>
                  </select>
                  
                  <select
                    value={selectedUrgency}
                    onChange={(e) => setSelectedUrgency(e.target.value)}
                    className="form-select"
                  >
                    <option value="">æ‰€æœ‰ç´§æ€¥ç¨‹åº¦</option>
                    <option value="low">ä½</option>
                    <option value="medium">ä¸­</option>
                    <option value="high">é«˜</option>
                    <option value="urgent">ç´§æ€¥</option>
                  </select>
                </div>
              </div>
            </div>
          </div>

          {/* ç»Ÿè®¡ä¿¡æ¯ */}
          <div className="stats-grid" style={{ marginBottom: '2rem' }}>
            <div className="stat-card info">
              <div className="stat-header">
                <div className="stat-info">
                  <div className="stat-title">å¯ç”¨æœºä¼š</div>
                  <div className="stat-value">{filteredRequests.length}</div>
                </div>
                <div className="stat-icon info">ğŸ”</div>
              </div>
            </div>
            <div className="stat-card warning">
              <div className="stat-header">
                <div className="stat-info">
                  <div className="stat-title">ç´§æ€¥è¯·æ±‚</div>
                  <div className="stat-value">{filteredRequests.filter(r => r.urgency === 'urgent').length}</div>
                </div>
                <div className="stat-icon warning">ğŸš¨</div>
              </div>
            </div>
          </div>
          
          {/* è¯·æ±‚åˆ—è¡¨ */}
          <div className="card">
            <div className="card-header">
              <h3 className="card-title">å¿—æ„¿æœåŠ¡æœºä¼š</h3>
              <p className="card-subtitle">ç‚¹å‡»"ç”³è¯·å¿—æ„¿"æ¥å¸®åŠ©æœ‰éœ€è¦çš„äºº</p>
            </div>
            <div className="card-body">
              {filteredRequests.length > 0 ? (
                <div className="request-list">
                  {filteredRequests.map(request => (
                    <div key={request.id} className="request-item">
                      <div className="request-header">
                        <h4 className="request-title">{request.title}</h4>
                        <div className={`urgency-badge ${request.urgency}`}>
                          {getUrgencyName(request.urgency, t)}
                        </div>
                      </div>
                      
                      <div className="request-meta">
                        <span className="category-tag">{getCategoryName(request.category, t)}</span>
                        <span className="meta-item">ğŸ“ {request.location}</span>
                        <span className="meta-item">ğŸ‘¤ {request.requesterName}</span>
                        <span className="meta-item">{getTimeAgo(request.createdAt)}</span>
                      </div>

                      <div className="request-description">
                        {request.description}
                      </div>

                      {request.expectedDate && (
                        <div className="request-timing">
                          <strong>é¢„æœŸæ—¶é—´ï¼š</strong>
                          {new Date(request.expectedDate).toLocaleDateString('zh-CN')}
                          {request.expectedTime && ` ${request.expectedTime}`}
                        </div>
                      )}

                      <div className="request-actions">
                        <button 
                          className="btn btn-primary"
                          onClick={() => handleApplyVolunteer(request.id)}
                        >
                          ç”³è¯·å¿—æ„¿
                        </button>
                        <button 
                          className={`btn ${isRequestShortlisted(request.id) ? 'btn-warning' : 'btn-outline'}`}
                          onClick={() => handleToggleShortlist(request)}
                        >
                          {isRequestShortlisted(request.id) ? `â­ ${t('csr.search.shortlisted')}` : `â˜† ${t('csr.search.shortlist')}`}
                        </button>
                        <button 
                          className="btn btn-secondary"
                          onClick={() => handleViewDetail(request)}
                        >
                          æŸ¥çœ‹è¯¦æƒ…
                        </button>
                      </div>
                    </div>
                  ))}
                </div>
              ) : (
                <div className="empty-state">
                  <div className="empty-state-icon">ğŸ”</div>
                  <div className="empty-state-title">
                    {requests.length === 0 ? 'æš‚æ— å¿—æ„¿æœºä¼š' : 'æ²¡æœ‰åŒ¹é…çš„ç»“æœ'}
                  </div>
                  <div className="empty-state-description">
                    {requests.length === 0 
                      ? 'ç›®å‰æ²¡æœ‰å¾…åŒ¹é…çš„å¿—æ„¿æœåŠ¡è¯·æ±‚ï¼Œè¯·ç¨åå†æŸ¥çœ‹'
                      : 'å°è¯•è°ƒæ•´æœç´¢æ¡ä»¶æˆ–æ¸…é™¤è¿‡æ»¤å™¨'
                    }
                  </div>
                  {requests.length > 0 && (
                    <button 
                      className="btn btn-secondary"
                      onClick={() => {
                        setSearchTerm('');
                        setSelectedCategory('');
                        setSelectedUrgency('');
                      }}
                    >
                      æ¸…é™¤è¿‡æ»¤å™¨
                    </button>
                  )}
                </div>
              )}
            </div>
          </div>
        </div>
      </div>

      {/* è¯·æ±‚è¯¦æƒ…æ¨¡æ€æ¡† */}
      {showRequestDetail && (
        <RequestDetailModal 
          request={showRequestDetail}
          onClose={() => setShowRequestDetail(null)}
        />
      )}
    </div>
  );
};

// è¾…åŠ©å‡½æ•° - ç°åœ¨åœ¨ç»„ä»¶å†…éƒ¨ä½¿ç”¨tå‡½æ•°
const getCategoryName = (category, t) => {
  return t(`category.${category}`, category);
};

const getUrgencyName = (urgency, t) => {
  return t(`urgency.${urgency}`, urgency);
};

const getTimeAgo = (dateString) => {
  const now = new Date();
  const past = new Date(dateString);
  const diffInHours = Math.floor((now - past) / (1000 * 60 * 60));
  
  if (diffInHours < 1) return 'åˆšåˆš';
  if (diffInHours < 24) return `${diffInHours}å°æ—¶å‰`;
  
  const diffInDays = Math.floor(diffInHours / 24);
  if (diffInDays < 7) return `${diffInDays}å¤©å‰`;
  
  const diffInWeeks = Math.floor(diffInDays / 7);
  return `${diffInWeeks}å‘¨å‰`;
};

export default CSRSearch;
