// CategoryManagement.jsx
import React, { useState } from 'react';
import { useLanguage } from '../../context/LanguageContext';

const CategoryManagement = () => {
  const { t } = useLanguage();
  const [categories, setCategories] = useState([
    { id: 1, name: t('category.medical'), count: 12 },
    { id: 2, name: t('category.transport'), count: 8 },
    { id: 3, name: t('category.shopping'), count: 15 },
    { id: 4, name: t('category.household'), count: 6 }
  ]);
  const [newCategory, setNewCategory] = useState('');

  const handleAddCategory = () => {
    if (newCategory.trim()) {
      const newCat = {
        id: Date.now(),
        name: newCategory,
        count: 0
      };
      setCategories([...categories, newCat]);
      setNewCategory('');
    }
  };

  const handleDeleteCategory = (id) => {
    setCategories(categories.filter(cat => cat.id !== id));
  };

  return (
    <div className="admin-page">
      <div className="page-header">
        <h1 className="page-title">{t('admin.categories.title')}</h1>
        <p className="page-subtitle">{t('admin.categories.subtitle')}</p>
      </div>

      <div className="card">
        <div className="card-header">
          <h3 className="card-title">{t('admin.categories.addNew')}</h3>
        </div>
        <div className="card-body">
          <div className="input-group">
            <input 
              type="text" 
              className="form-input"
              placeholder={t('admin.categories.placeholder')}
              value={newCategory}
              onChange={(e) => setNewCategory(e.target.value)}
              onKeyPress={(e) => e.key === 'Enter' && handleAddCategory()}
            />
            <button 
              className="btn btn-primary"
              onClick={handleAddCategory}
              disabled={!newCategory.trim()}
            >
              <span className="btn-icon">+</span>
              {t('admin.categories.add')}
            </button>
          </div>
        </div>
      </div>

      <div className="card">
        <div className="card-header">
          <h3 className="card-title">{t('admin.categories.existing')}</h3>
          <span className="badge">{categories.length} {t('admin.categories.count')}</span>
        </div>
        <div className="card-body">
          <div className="grid-list">
            {categories.map(category => (
              <div key={category.id} className="grid-item">
                <div className="item-content">
                  <div className="item-icon">üìÅ</div>
                  <div className="item-info">
                    <h4 className="item-title">{category.name}</h4>
                    <p className="item-subtitle">{category.count} {t('admin.categories.relatedRequests')}</p>
                  </div>
                </div>
                <button 
                  className="btn btn-danger btn-sm"
                  onClick={() => handleDeleteCategory(category.id)}
                >
                  {t('common.delete')}
                </button>
              </div>
            ))}
          </div>
        </div>
      </div>
    </div>
  );
};

export default CategoryManagement;
