// 导航组件
// src/components/Layout/Navbar.jsx
import React from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import { useLanguage } from '../../context/LanguageContext';
import LanguageSwitcher from '../Common/LanguageSwitcher';

const Navbar = ({ userType, user }) => {
  const { logout } = useAuth();
  
  const handleLogout = () => {
    logout();
    window.location.href = '/login';
  };
  
  return (
    <nav className="navbar">
      <div className="navbar-container">
        <Link to="/" className="nav-brand">
          <div className="nav-brand-icon">C</div>
          CSR志愿者匹配系统
        </Link>
        
        <div className="nav-main">
          <ul className="nav-links">
            {userType === 'pin' && (
              <>
                <li><Link to="/pin/dashboard" className="nav-link">我的请求</Link></li>
                <li><Link to="/pin/history" className="nav-link">历史记录</Link></li>
              </>
            )}
            
            {userType === 'csr' && (
              <>
                <li><Link to="/csr/search" className="nav-link">搜索机会</Link></li>
                <li><Link to="/csr/shortlist" className="nav-link">收藏夹</Link></li>
                <li><Link to="/csr/history" className="nav-link">服务历史</Link></li>
              </>
            )}
            
            {userType === 'admin' && (
              <>
                <li><Link to="/admin/dashboard" className="nav-link">用户管理</Link></li>
                <li><Link to="/admin/categories" className="nav-link">类别管理</Link></li>
                <li><Link to="/admin/reports" className="nav-link">数据报告</Link></li>
              </>
            )}
          </ul>
          
          <div className="nav-user">
            <div className="user-info">
              <div className="user-name">{user?.name || '用户'}</div>
              <div className="user-role">{userType}</div>
            </div>
            <div className="user-avatar">
              {user?.name?.charAt(0) || 'U'}
            </div>
            <button onClick={handleLogout} className="btn btn-secondary">
              退出
            </button>
          </div>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
