// src/services/dataService.js

import apiService from './apiService';

export class DataService {
  // ÁºìÂ≠òÊï∞ÊçÆ
  static cache = {
    categories: null,
    users: null,
    requests: null,
    shortlists: null,
    lastFetch: {}
  };

  // ÁºìÂ≠òÊúâÊïàÊúüÔºà5ÂàÜÈíüÔºâ
  static CACHE_DURATION = 5 * 60 * 1000;

  // Ê£ÄÊü•ÁºìÂ≠òÊòØÂê¶ÊúâÊïà
  static isCacheValid(key) {
    const lastFetch = this.cache.lastFetch[key];
    return lastFetch && (Date.now() - lastFetch) < this.CACHE_DURATION;
  }

  // Êõ¥Êñ∞ÁºìÂ≠ò
  static updateCache(key, data) {
    this.cache[key] = data;
    this.cache.lastFetch[key] = Date.now();
  }

  // Ê∏ÖÁ©∫ÁºìÂ≠ò
  static clearCache(key = null) {
    if (key) {
      this.cache[key] = null;
      delete this.cache.lastFetch[key];
    } else {
      this.cache = {
        categories: null,
        users: null,
        requests: null,
        shortlists: null,
        lastFetch: {}
      };
    }
  }

  // ÂàÜÁ±ªÊï∞ÊçÆ
  static async getCategories() {
    try {
      // Ê£ÄÊü•ÁºìÂ≠ò
      if (this.isCacheValid('categories') && this.cache.categories) {
        return this.cache.categories;
      }

      const categories = await apiService.getCategories();
      
      // ËΩ¨Êç¢Êï∞ÊçÆÊ†ºÂºè‰ª•ÂÖºÂÆπÁé∞Êúâ‰ª£Á†Å
      const formattedCategories = categories.map(cat => ({
        id: cat.name || cat._id,
        name: `category.${cat.name}`,
        icon: cat.icon || 'üìÅ',
        color: this.getColorFromHex(cat.color || '#42a5f5')
      }));

      this.updateCache('categories', formattedCategories);
      return formattedCategories;
    } catch (error) {
      console.error('Ëé∑ÂèñÂàÜÁ±ªÂ§±Ë¥•Ôºå‰ΩøÁî®ÈªòËÆ§Êï∞ÊçÆ:', error);
      // ‰ΩøÁî®ÈªòËÆ§Êï∞ÊçÆ‰Ωú‰∏∫ÂêéÂ§á
      const defaultCategories = [
        { id: 'medical', name: 'category.medical', icon: 'üè•' },
        { id: 'transportation', name: 'category.transport', icon: 'üöó' },
        { id: 'shopping', name: 'category.shopping', icon: 'üõí' },
        { id: 'household', name: 'category.household', icon: 'üè†' },
        { id: 'companion', name: 'category.companion', icon: 'üë•' },
        { id: 'technology', name: 'category.technology', icon: 'üíª' },
        { id: 'education', name: 'category.education', icon: 'üìö' },
        { id: 'other', name: 'category.other', icon: 'üìù' }
      ];
      return defaultCategories;
    }
  }

  // Â∞ÜÂçÅÂÖ≠ËøõÂà∂È¢úËâ≤ËΩ¨Êç¢‰∏∫ËØ≠‰πâÂåñÈ¢úËâ≤Âêç
  static getColorFromHex(hexColor) {
    const colorMap = {
      '#ff4757': 'danger',
      '#ffa726': 'warning', 
      '#42a5f5': 'primary',
      '#66bb6a': 'success',
      '#ab47bc': 'info',
      '#26c6da': 'secondary'
    };
    return colorMap[hexColor] || 'primary';
  }

  // Á¥ßÊÄ•Á®ãÂ∫¶Êï∞ÊçÆÔºàÈùôÊÄÅÊï∞ÊçÆÔºåÊó†ÈúÄAPIÔºâ
  static getUrgencyLevels() {
    return [
      { id: 'low', name: 'urgency.low', color: 'success' },
      { id: 'medium', name: 'urgency.medium', color: 'warning' },
      { id: 'high', name: 'urgency.high', color: 'danger' },
      { id: 'urgent', name: 'urgency.urgent', color: 'critical' }
    ];
  }

  // Áä∂ÊÄÅÊï∞ÊçÆÔºàÈùôÊÄÅÊï∞ÊçÆÔºåÊó†ÈúÄAPIÔºâ
  static getStatusOptions() {
    return [
      { id: 'pending', name: 'status.pending', color: 'warning' },
      { id: 'matched', name: 'status.matched', color: 'success' },
      { id: 'completed', name: 'status.completed', color: 'info' },
      { id: 'cancelled', name: 'status.cancelled', color: 'danger' }
    ];
  }

  // Ëé∑ÂèñÁî®Êà∑Êï∞ÊçÆ
  static async getUsers() {
    try {
      // Ê£ÄÊü•ÁºìÂ≠ò
      if (this.isCacheValid('users') && this.cache.users) {
        return this.cache.users;
      }

      const users = await apiService.getAllUsers();
      this.updateCache('users', users);
      return users;
    } catch (error) {
      console.error('Ëé∑ÂèñÁî®Êà∑Êï∞ÊçÆÂ§±Ë¥•:', error);
      return [];
    }
  }

  // Ëé∑ÂèñËØ∑Ê±ÇÊï∞ÊçÆ
  static async getRequests() {
    try {
      // Ê£ÄÊü•ÁºìÂ≠ò
      if (this.isCacheValid('requests') && this.cache.requests) {
        return this.cache.requests;
      }

      const requests = await apiService.getAllRequests();
      
      // ËΩ¨Êç¢Êï∞ÊçÆÊ†ºÂºè‰ª•ÂÖºÂÆπÁé∞Êúâ‰ª£Á†Å
      const formattedRequests = requests.map(req => ({
        id: req._id,
        title: req.title,
        description: req.description,
        category: req.category?.name || req.category,
        urgency: req.urgency,
        location: req.location,
        expectedDate: req.expectedDate ? new Date(req.expectedDate).toISOString().split('T')[0] : null,
        expectedTime: req.expectedTime,
        volunteersNeeded: req.volunteersNeeded,
        status: req.status,
        requesterId: req.requesterId?._id || req.requesterId,
        requesterName: req.requesterId?.name || req.requesterName,
        requesterEmail: req.requesterId?.email || req.requesterEmail,
        requesterPhone: req.requesterId?.phone || req.requesterPhone,
        requesterAddress: req.requesterId?.address || req.requesterAddress,
        volunteer: req.assignedVolunteer?.name || req.volunteer,
        viewCount: req.viewCount || 0,
        shortlistCount: req.shortlistCount || 0,
        createdAt: req.createdAt,
        matchedAt: req.matchedAt
      }));

      this.updateCache('requests', formattedRequests);
      return formattedRequests;
    } catch (error) {
      console.error('Ëé∑ÂèñËØ∑Ê±ÇÊï∞ÊçÆÂ§±Ë¥•:', error);
      return [];
    }
  }

  // Ëé∑ÂèñÊî∂ËóèÂ§πÊï∞ÊçÆ
  static async getShortlists() {
    try {
      // Ê£ÄÊü•ÁºìÂ≠ò
      if (this.isCacheValid('shortlists') && this.cache.shortlists) {
        return this.cache.shortlists;
      }

      const shortlists = await apiService.getUserShortlist();
      this.updateCache('shortlists', shortlists);
      return shortlists;
    } catch (error) {
      console.error('Ëé∑ÂèñÊî∂ËóèÂ§πÊï∞ÊçÆÂ§±Ë¥•:', error);
      return [];
    }
  }

  // ‰øùÂ≠òÂàÜÁ±ªÊï∞ÊçÆ
  static async saveCategories(categories) {
    try {
      // Ê≥®ÊÑèÔºöËøôÈáåÈúÄË¶ÅÁÆ°ÁêÜÂëòÊùÉÈôê
      await apiService.updateCategory(categories);
      this.clearCache('categories');
    } catch (error) {
      console.error('‰øùÂ≠òÂàÜÁ±ªÊï∞ÊçÆÂ§±Ë¥•:', error);
      throw error;
