  // ‰øùÂ≠òÁî®Êà∑Êï∞ÊçÆ
  static async saveUsers(users) {
    try {
      // Ê≥®ÊÑèÔºöËøôÈáåÈúÄË¶ÅÁÆ°ÁêÜÂëòÊùÉÈôê
      // ÂÆûÈôÖÂÆûÁé∞‰∏≠ÔºåÁî®Êà∑Êï∞ÊçÆÈÄöÂ∏∏‰∏ç‰ºöÊâπÈáè‰øùÂ≠ò
      console.warn('ÊâπÈáè‰øùÂ≠òÁî®Êà∑Êï∞ÊçÆÊöÇÊú™ÂÆûÁé∞');
      this.clearCache('users');
    } catch (error) {
      console.error('‰øùÂ≠òÁî®Êà∑Êï∞ÊçÆÂ§±Ë¥•:', error);
