        requesterId: req.requesterId?._id || req.requesterId,
        volunteer: req.assignedVolunteer?.name || req.volunteer,
        viewCount: req.viewCount || 0,
        shortlistCount: req.shortlistCount || 0,
        createdAt: req.createdAt,
        matchedAt: req.matchedAt
      }));
    } catch (error) {
      console.error('Ëé∑ÂèñÁî®Êà∑ËØ∑Ê±ÇÂ§±Ë¥•:', error);
      return [];
    }
  }

  // Ëé∑ÂèñÁî®Êà∑ÁöÑÊî∂ËóèÂ§π
  static async getUserShortlists(userId) {
    try {
      const shortlists = await apiService.getUserShortlist();
      return shortlists.filter(shortlist => shortlist.userId === userId);
    } catch (error) {
      console.error('Ëé∑ÂèñÁî®Êà∑Êî∂ËóèÂ§πÂ§±Ë¥•:', error);
      return [];
    }
  }

  // Êó∂Èó¥Ê†ºÂºèÂåñÂ∑•ÂÖ∑
  static getTimeAgo(dateString, t = null) {
    if (!dateString) return t ? t('common.unknown') : 'Êú™Áü•';
    
    const now = new Date();
    const past = new Date(dateString);
    const diffInMinutes = Math.floor((now - past) / (1000 * 60));
    const diffInHours = Math.floor(diffInMinutes / 60);
    const diffInDays = Math.floor(diffInHours / 24);
    
    if (diffInMinutes < 1) {
      return t ? t('time.justNow') : 'ÂàöÂàö';
    }
    
    if (diffInMinutes < 60) {
      return t ? t('time.minutesAgo', { count: diffInMinutes }) : `${diffInMinutes}ÂàÜÈíüÂâç`;
    }
    
    if (diffInHours < 24) {
      return t ? t('time.hoursAgo', { count: diffInHours }) : `${diffInHours}Â∞èÊó∂Ââç`;
    }
    
    if (diffInDays < 7) {
      return t ? t('time.daysAgo', { count: diffInDays }) : `${diffInDays}Â§©Ââç`;
    }
    
    const diffInWeeks = Math.floor(diffInDays / 7);
    return t ? t('time.weeksAgo', { count: diffInWeeks }) : `${diffInWeeks}Âë®Ââç`;
  }

