// 4. PINÂéÜÂè≤ËÆ∞ÂΩïÈ°µÈù¢
// src/pages/PIN/History.jsx
import React, { useState, useEffect } from 'react';
import { useAuth } from '../../context/AuthContext';
import { useLanguage } from '../../context/LanguageContext';
import Navbar from '../../components/Layout/Navbar';
import RequestDetailModal from '../../components/PIN/RequestDetailModal';
import '../../styles/history.css';

const PINHistory = () => {
  const { user } = useAuth();
  const { t } = useLanguage();
  const [historyRecords, setHistoryRecords] = useState([]);
  const [filteredRecords, setFilteredRecords] = useState([]);
  const [showRequestDetail, setShowRequestDetail] = useState(null);
  const [filters, setFilters] = useState({
    serviceType: '',
    startDate: '',
    endDate: ''
  });

  // Âä†ËΩΩÂéÜÂè≤ËÆ∞ÂΩï
  useEffect(() => {
    if (user?.id) {
      loadHistoryRecords();
    }
  }, [user?.id]);

  // Â∫îÁî®ËøáÊª§Âô®
  useEffect(() => {
    applyFilters();
  }, [historyRecords, filters]);

  const loadHistoryRecords = () => {
    // ‰ªélocalStorageËé∑ÂèñÊâÄÊúâËØ∑Ê±Ç
    const allRequests = JSON.parse(localStorage.getItem('userRequests') || '[]');
    
    // ËøáÊª§Âá∫ÂΩìÂâçÁî®Êà∑Â∑≤ÂÆåÊàêÔºàmatchedÔºâÁöÑËØ∑Ê±Ç
    const userCompletedRequests = allRequests.filter(request => 
      request.requesterId === user.id && 
      request.status === 'matched' &&
      request.matchedAt // Á°Æ‰øùÊúâÂåπÈÖçÊó∂Èó¥
    );

    // ÊåâÂåπÈÖçÊó∂Èó¥ÈôçÂ∫èÊéíÂ∫è
    const sortedRecords = userCompletedRequests.sort((a, b) => 
      new Date(b.matchedAt) - new Date(a.matchedAt)
    );

    setHistoryRecords(sortedRecords);
  };

  const applyFilters = () => {
    let filtered = [...historyRecords];

    // ÊåâÊúçÂä°Á±ªÂûãËøáÊª§
    if (filters.serviceType) {
      filtered = filtered.filter(record => record.category === filters.serviceType);
    }

    // ÊåâÊó•ÊúüËåÉÂõ¥ËøáÊª§
    if (filters.startDate) {
      filtered = filtered.filter(record => {
        const matchDate = new Date(record.matchedAt);
        const startDate = new Date(filters.startDate);
        return matchDate >= startDate;
      });
    }

    if (filters.endDate) {
      filtered = filtered.filter(record => {
        const matchDate = new Date(record.matchedAt);
        const endDate = new Date(filters.endDate);
        endDate.setHours(23, 59, 59, 999); // ÂåÖÂê´ÁªìÊùüÊó•ÊúüÁöÑÊï¥Â§©
        return matchDate <= endDate;
      });
    }

    setFilteredRecords(filtered);
  };

  const handleFilterChange = (field, value) => {
    setFilters(prev => ({
      ...prev,
      [field]: value
    }));
  };

  const handleViewDetail = (record) => {
    setShowRequestDetail(record);
  };

  const getCategoryText = (category) => {
    // Use translation system directly for all categories
    return t(`category.${category}`) || category;
  };

  const getUrgencyText = (urgency) => {
    const urgencyMap = {
      low: '‰Ωé',
      medium: '‰∏≠',
      high: 'È´ò',
      urgent: 'Á¥ßÊÄ•'
    };
    return urgencyMap[urgency] || urgency;
  };

  const getUrgencyColor = (urgency) => {
    const colorMap = {
      low: 'success',
      medium: 'warning',
      high: 'danger',
      urgent: 'critical'
    };
    return colorMap[urgency] || 'default';
  };

  return (
    <div className="page-container">
      <Navbar userType="pin" user={user} />
      
      <div className="main-content">
        <div className="history-page">
          <div className="page-header">
            <div className="header-content">
              <h1 className="page-title">{t('pin.history.title')}</h1>
              <p className="page-subtitle">Êü•ÁúãÊÇ®ÁöÑÂéÜÂè≤ÂåπÈÖçËÆ∞ÂΩïÂíåÂÆåÊàêÁöÑÊúçÂä°</p>
            </div>
          </div>

          {/* Á≠õÈÄâÂô® */}
          <div className="filters-section">
            <div className="filters">
              <div className="filter-group">
                <label>ÊúçÂä°Á±ªÂûã</label>
                <select 
                  value={filters.serviceType} 
                  onChange={(e) => handleFilterChange('serviceType', e.target.value)}
                  className="filter-select"
                >
                  <option value="">{t('pin.history.allServiceTypes')}</option>
                  <option value="medical">{t('category.medical')}</option>
                  <option value="transport">{t('category.transport')}</option>
                  <option value="shopping">{t('category.shopping')}</option>
                  <option value="household">{t('category.household')}</option>
                  <option value="companion">{t('category.companion')}</option>
                </select>
              </div>
              
              <div className="filter-group">
                <label>ÂºÄÂßãÊó•Êúü</label>
                <input 
                  type="date" 
                  value={filters.startDate}
                  onChange={(e) => handleFilterChange('startDate', e.target.value)}
                  className="filter-input"
                />
              </div>
              
              <div className="filter-group">
                <label>ÁªìÊùüÊó•Êúü</label>
                <input 
                  type="date" 
                  value={filters.endDate}
                  onChange={(e) => handleFilterChange('endDate', e.target.value)}
                  className="filter-input"
                />
              </div>
              
              <div className="filter-actions">
                <button 
                  className="btn btn-secondary"
                  onClick={() => setFilters({ serviceType: '', startDate: '', endDate: '' })}
                >
                  Ê∏ÖÈô§ËøáÊª§Âô®
                </button>
              </div>
            </div>
            
            <div className="results-count">
              ÊâæÂà∞ {filteredRecords.length} Êù°ÂéÜÂè≤ËÆ∞ÂΩï
            </div>
          </div>

          {/* ÂéÜÂè≤ËÆ∞ÂΩïÂàóË°® */}
          {filteredRecords.length > 0 ? (
            <div className="history-list">
              {filteredRecords.map((record) => (
                <div key={record.id} className="history-card">
                  <div className="card-header">
                    <div className="card-title">
                      <h3>{record.title}</h3>
                      <div className="card-meta">
                        <span className="category-badge">{getCategoryText(record.category)}</span>
                        <span className={`urgency-badge urgency-${getUrgencyColor(record.urgency)}`}>
                          {getUrgencyText(record.urgency)}
                        </span>
                      </div>
                    </div>
                    <div className="match-info">
                      <div className="volunteer-name">
                        üë§ ÂøóÊÑøËÄÖ: {record.volunteer || 'Êú™Áü•'}
                      </div>
                      <div className="match-date">
                        ‚úÖ ÂåπÈÖçÊó∂Èó¥: {new Date(record.matchedAt).toLocaleDateString('zh-CN')}
                      </div>
                    </div>
                  </div>
                  
                  <div className="card-body">
                    <p className="description">{record.description}</p>
                    <div className="service-details">
                      <div className="detail-item">
                        <span className="label">üìç ÊúçÂä°Âú∞ÁÇπ:</span>
                        <span className="value">{record.location?.address || record.location || 'ÂæÖÁ°ÆÂÆö'}</span>
                      </div>
                      {record.expectedDate && (
                        <div className="detail-item">
                          <span className="label">üïí È¢ÑÊúüÊó∂Èó¥:</span>
                          <span className="value">
                            {new Date(record.expectedDate).toLocaleDateString('zh-CN')}
                            {record.expectedTime && ` ${record.expectedTime}`}
                          </span>
                        </div>
                      )}
                      <div className="detail-item">
                        <span className="label">üìä ÁªüËÆ°‰ø°ÊÅØ:</span>
                        <span className="value">
                          ÊµèËßà {record.viewCount || 0} Ê¨° ‚Ä¢ Êî∂Ëóè {record.shortlistCount || 0} Ê¨°
                        </span>
                      </div>
                    </div>
                  </div>
                  
                  <div className="card-actions">
                    <button 
                      className="btn btn-primary"
                      onClick={() => handleViewDetail(record)}
                    >
                      Êü•ÁúãËØ¶ÊÉÖ
                    </button>
                  </div>
                </div>
              ))}
            </div>
          ) : (
            <div className="empty-state">
              <div className="empty-state-icon">üìã</div>
              <div className="empty-state-title">
                {historyRecords.length === 0 ? 'ÊöÇÊó†ÂéÜÂè≤ËÆ∞ÂΩï' : 'Ê≤°ÊúâÁ¨¶ÂêàÊù°‰ª∂ÁöÑËÆ∞ÂΩï'}
              </div>
              <div className="empty-state-description">
                {historyRecords.length === 0 
                  ? 'ÊÇ®ËøòÊ≤°ÊúâÂÆåÊàê‰ªª‰ΩïÂåπÈÖçÁöÑÊúçÂä°ËØ∑Ê±Ç„ÄÇÂàõÂª∫ËØ∑Ê±ÇÂπ∂Á≠âÂæÖÂøóÊÑøËÄÖÁî≥ËØ∑ÂêßÔºÅ'
                  : 'Â∞ùËØïË∞ÉÊï¥ËøáÊª§Êù°‰ª∂Êü•ÁúãÊõ¥Â§öËÆ∞ÂΩï'
                }
              </div>
              {historyRecords.length === 0 && (
                <a href="/pin/dashboard" className="btn btn-primary">
                  ÂàõÂª∫ËØ∑Ê±Ç
                </a>
              )}
            </div>
          )}
        </div>
      </div>

      {/* ËØ∑Ê±ÇËØ¶ÊÉÖÊ®°ÊÄÅÊ°Ü */}
      {showRequestDetail && (
        <RequestDetailModal 
          request={showRequestDetail}
          onClose={() => setShowRequestDetail(null)}
        />
      )}
    </div>
  );
};

export default PINHistory;
