// Login.jsx
import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';

const Login = () => {
  const [formData, setFormData] = useState({
    email: '',
    password: '',
    userType: ''
  });
  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  
  const { login } = useAuth();
  const navigate = useNavigate();
  
  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setIsLoading(true);
    
    if (!formData.userType) {
      setError('ËØ∑ÈÄâÊã©Áî®Êà∑Á±ªÂûã');
      setIsLoading(false);
      return;
    }
    
    // Ê®°ÊãüÁôªÂΩïËøáÁ®ã
    setTimeout(() => {
      setIsLoading(false);
      switch (formData.userType) {
        case 'pin':
          navigate('/pin/dashboard');
          break;
        case 'csr':
          navigate('/csr/search');
          break;
        case 'admin':
          navigate('/admin/dashboard');
          break;
        default:
          navigate('/');
      }
    }, 1500);
  };
  
  const handleChange = (e) => {
    setFormData({
      ...formData,
      [e.target.name]: e.target.value
    });
  };

  const userTypes = [
    { value: 'pin', label: 'ÈúÄË¶ÅÂ∏ÆÂä©ËÄÖ (PIN)', icon: 'üôã', description: 'ÂØªÊ±ÇÂøóÊÑøÊúçÂä°Â∏ÆÂä©' },
    { value: 'csr', label: 'ÂøóÊÑøËÄÖ (CSR)', icon: 'ü§ù', description: 'Êèê‰æõÂøóÊÑøÊúçÂä°' },
    { value: 'admin', label: 'Âπ≥Âè∞ÁÆ°ÁêÜÂëò', icon: 'üë®‚Äçüíº', description: 'ÁÆ°ÁêÜÁ≥ªÁªüËøêËê•' }
  ];
  
  return (
    <div className="auth-container">
      <div className="auth-background">
        <div className="auth-hero">
          <div className="hero-icon">ü§ù</div>
          <h1 className="hero-title">CSRÂøóÊÑøËÄÖÂåπÈÖçÁ≥ªÁªü</h1>
          <p className="hero-subtitle">ËøûÊé•ÈúÄË¶ÅÂ∏ÆÂä©ÁöÑ‰∫∫‰∏éÁÉ≠ÂøÉÂøóÊÑøËÄÖ</p>
        </div>
      </div>
      
      <div className="auth-card">
        <div className="auth-header">
          <h2 className="auth-title">Ê¨¢ËøéÂõûÊù•</h2>
          <p className="auth-subtitle">ËØ∑ÁôªÂΩïÊÇ®ÁöÑË¥¶Êà∑</p>
        </div>
        
        {error && (
          <div className="alert alert-error">
            <span className="alert-icon">‚ö†Ô∏è</span>
            {error}
          </div>
        )}
        
        <form className="auth-form" onSubmit={handleSubmit}>
          <div className="form-group">
            <label className="form-label">ÈÄâÊã©Ë∫´‰ªΩ</label>
            <div className="user-type-grid">
              {userTypes.map(type => (
                <label 
                  key={type.value}
                  className={`user-type-card ${
                    formData.userType === type.value ? 'selected' : ''
                  }`}
                >
                  <input
                    type="radio"
                    name="userType"
                    value={type.value}
                    checked={formData.userType === type.value}
                    onChange={handleChange}
                    className="sr-only"
                  />
                  <div className="user-type-icon">{type.icon}</div>
                  <div className="user-type-info">
                    <div className="user-type-label">{type.label}</div>
                    <div className="user-type-desc">{type.description}</div>
                  </div>
                </label>
              ))}
            </div>
          </div>
          
          <div className="form-group">
            <label className="form-label">ÈÇÆÁÆ±Âú∞ÂùÄ</label>
            <div className="input-with-icon">
              <span className="input-icon">üìß</span>
              <input 
                type="email" 
                name="email"
                className="form-input"
                placeholder="ËØ∑ËæìÂÖ•ÈÇÆÁÆ±Âú∞ÂùÄ"
                value={formData.email}
                onChange={handleChange}
                required
              />
            </div>
          </div>
          
          <div className="form-group">
            <label className="form-label">ÂØÜÁ†Å</label>
            <div className="input-with-icon">
              <span className="input-icon">üîí</span>
              <input 
                type="password" 
                name="password"
                className="form-input"
                placeholder="ËØ∑ËæìÂÖ•ÂØÜÁ†Å"
                value={formData.password}
                onChange={handleChange}
                required
              />
            </div>
          </div>
          
          <button 
            type="submit" 
            className={`btn btn-primary btn-full ${isLoading ? 'loading' : ''}`}
            disabled={isLoading}
          >
            {isLoading ? (
              <>
                <span className="loading-spinner"></span>
                ÁôªÂΩï‰∏≠...
              </>
            ) : (
              'ÁôªÂΩïË¥¶Êà∑'
            )}
          </button>
        </form>
        
        <div className="auth-footer">
          <p className="auth-link">
            ËøòÊ≤°ÊúâË¥¶Êà∑Ôºü <Link to="/register" className="link">Á´ãÂç≥Ê≥®ÂÜå</Link>
          </p>
          <div className="demo-accounts">
            <p className="demo-title">ÊµãËØïË¥¶Êà∑Ôºà‰ªªÊÑèÂØÜÁ†ÅÔºâ:</p>
            <div className="demo-list">
              <span>pin@test.com</span>
              <span>csr@test.com</span>
              <span>admin@test.com</span>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Login;
