// Áî®Êà∑Ê≥®ÂÜåÈ°µÈù¢
// src/pages/Auth/Register.jsx
import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import { useLanguage } from '../../context/LanguageContext';
import LanguageSwitcher from '../../components/Common/LanguageSwitcher';

const Register = () => {
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    password: '',
    confirmPassword: '',
    userType: '',
    phone: '',
    organization: '', // ‰ªÖCSRÂíåAdminÈúÄË¶Å
    address: '',
    birthDate: '', // ‰ªÖPINÁî®Êà∑ÈúÄË¶Å
    emergencyContact: '', // ‰ªÖPINÁî®Êà∑ÈúÄË¶Å
    skills: [], // ‰ªÖCSRÁî®Êà∑ÈúÄË¶Å
    adminCode: '' // ‰ªÖAdminÈúÄË¶Å
  });

  const [error, setError] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [step, setStep] = useState(1); // Â§öÊ≠•È™§Ê≥®ÂÜå

  const { register } = useAuth();
  const { t } = useLanguage();
  const navigate = useNavigate();

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');

    // Âü∫Á°ÄÈ™åËØÅ
    if (!formData.userType) {
      setError(t('auth.register.error.userType'));
      return;
    }

    if (formData.password !== formData.confirmPassword) {
      setError(t('auth.register.error.passwordMismatch'));
      return;
    }

    if (formData.password.length < 6) {
      setError(t('auth.register.error.passwordTooShort'));
      return;
    }

    // ÁâπÊÆäÈ™åËØÅÂ∑≤ÁßªÈô§Ôºà‰∏çÂÖÅËÆ∏Ê≥®ÂÜåÁÆ°ÁêÜÂëòÔºâ

    setIsLoading(true);

    const result = await register(formData);

    if (result.success) {
      // Ê≥®ÂÜåÊàêÂäüÂêéÊ†πÊçÆÁî®Êà∑Á±ªÂûãÈáçÂÆöÂêë
      switch (formData.userType) {
        case 'pin':
          navigate('/pin/dashboard');
          break;
        case 'csr':
          navigate('/csr/search');
          break;

        default:
          navigate('/');
      }
    } else {
      // Â§ÑÁêÜÈîôËØØ‰ª£Á†ÅÂπ∂ÊòæÁ§∫Áõ∏Â∫îÁöÑÁøªËØë‰ø°ÊÅØ
      let errorMessage = t('common.error');
      if (result.errorCode) {
        switch (result.errorCode) {
          case 'EMAIL_ALREADY_EXISTS':
            errorMessage = t('auth.register.error.emailExists');
            break;
          default:
            errorMessage = result.error || t('common.error');
        }
      } else {
        errorMessage = result.error || t('common.error');
      }
      setError(errorMessage);
    }

    setIsLoading(false);
  };

  const handleChange = (e) => {
    const { name, value, type, checked } = e.target;

    if (name === 'skills') {
      if (checked) {
        setFormData(prev => ({
          ...prev,
          skills: [...prev.skills, value]
        }));
      } else {
        setFormData(prev => ({
          ...prev,
          skills: prev.skills.filter(skill => skill !== value)
        }));
      }
    } else {
      setFormData(prev => ({
        ...prev,
        [name]: value
      }));
    }
  };

  const nextStep = () => {
    if (step === 1) {
      if (!formData.name || !formData.email || !formData.userType) {
        setError(t('auth.register.error.requiredFields'));
        return;
      }
    }
    setError('');
    setStep(step + 1);
  };

  const prevStep = () => {
    setError('');
    setStep(step - 1);
  };

  return (
    <div className="auth-container">
      <div className="auth-background">
        <div className="auth-hero">
          <div className="hero-icon">üìù</div>
          <h1 className="hero-title">{t('app.title')}</h1>
          <p className="hero-subtitle">ÂºÄÂßãÊÇ®ÁöÑÂøóÊÑøÊúçÂä°‰πãÊóÖ</p>
        </div>
      </div>

      <div className="auth-card" style={{ maxWidth: '500px' }}>
        <div className="auth-header">
          <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: '1rem' }}>
            <div>
              <h2 className="auth-title">Âä†ÂÖ•CSRÂøóÊÑøËÄÖÂåπÈÖçÁ≥ªÁªü</h2>
              <p className="auth-subtitle">ÂàõÂª∫ÊÇ®ÁöÑË¥¶Êà∑ÔºåÂºÄÂßãÂ∏ÆÂä©‰ªñ‰∫∫</p>
            </div>
            <LanguageSwitcher />
          </div>
        </div>

        {/* Ê≠•È™§ÊåáÁ§∫Âô® */}
        <div style={{ display: 'flex', justifyContent: 'center', marginBottom: '2rem' }}>
          {[1, 2, 3].map(stepNum => (
            <div 
              key={stepNum}
              style={{
                width: '30px',
                height: '30px',
                borderRadius: '50%',
                backgroundColor: step >= stepNum ? 'var(--primary-color)' : 'var(--gray-300)',
                color: step >= stepNum ? 'white' : 'var(--gray-600)',
                display: 'flex',
                alignItems: 'center',
                justifyContent: 'center',
                margin: '0 10px',
                fontSize: '14px',
                fontWeight: '600'
              }}
            >
              {stepNum}
            </div>
          ))}
        </div>

        {error && (
          <div className="message message-error">
            {error}
          </div>
        )}

        <form className="auth-form" onSubmit={handleSubmit}>
          {/* Á¨¨‰∏ÄÊ≠•ÔºöÂü∫Êú¨‰ø°ÊÅØ */}
          {step === 1 && (
            <>
              <div className="form-group">
                <label className="form-label required">ÂßìÂêç</label>
                <input 
                  type="text" 
                  name="name"
                  className="form-input"
                  placeholder="ËØ∑ËæìÂÖ•ÊÇ®ÁöÑÁúüÂÆûÂßìÂêç"
                  value={formData.name}
                  onChange={handleChange}
                  required
                />
              </div>

              <div className="form-group">
                <label className="form-label required">ÈÇÆÁÆ±</label>
                <input 
                  type="email" 
                  name="email"
                  className="form-input"
                  placeholder="ËØ∑ËæìÂÖ•ÈÇÆÁÆ±Âú∞ÂùÄ"
                  value={formData.email}
                  onChange={handleChange}
                  required
                />
              </div>

              <div className="form-group">
                <label className="form-label required">Áî®Êà∑Á±ªÂûã</label>
                <select 
                  name="userType"
                  className="form-select"
                  value={formData.userType}
                  onChange={handleChange}
                  required
                >
                  <option value="">ÈÄâÊã©ÊÇ®ÁöÑË∫´‰ªΩ</option>
                  <option value="pin">Person-In-Need (PIN) - ÈúÄË¶ÅÂ∏ÆÂä©ÁöÑ‰∏™‰∫∫</option>
                  <option value="csr">CSR‰ª£Ë°® - ‰ºÅ‰∏öÂøóÊÑøËÄÖ</option>
                </select>
              </div>

              <div className="form-group">
                <label className="form-label">ÊâãÊú∫Âè∑Á†Å</label>
                <input 
                  type="tel" 
                  name="phone"
                  className="form-input"
                  placeholder="ËØ∑ËæìÂÖ•ÊâãÊú∫Âè∑Á†Å"
                  value={formData.phone}
                  onChange={handleChange}
                />
              </div>

              <button type="button" className="btn btn-primary btn-full" onClick={nextStep}>
                ‰∏ã‰∏ÄÊ≠• ‚Üí
              </button>
            </>
          )}

          {/* Á¨¨‰∫åÊ≠•ÔºöÂØÜÁ†ÅËÆæÁΩÆ */}
          {step === 2 && (
            <>
              <div className="form-group">
                <label className="form-label required">ÂØÜÁ†Å</label>
                <input 
                  type="password" 
                  name="password"
                  className="form-input"
                  placeholder="Ëá≥Â∞ë6‰ΩçÂØÜÁ†Å"
                  value={formData.password}
                  onChange={handleChange}
                  required
                />
              </div>

              <div className="form-group">
                <label className="form-label required">Á°ÆËÆ§ÂØÜÁ†Å</label>
                <input 
                  type="password" 
                  name="confirmPassword"
                  className="form-input"
                  placeholder="ÂÜçÊ¨°ËæìÂÖ•ÂØÜÁ†Å"
                  value={formData.confirmPassword}
                  onChange={handleChange}
                  required
                />
              </div>

              <div className="form-group">
                <label className="form-label">Âú∞ÂùÄ</label>
                <input 
                  type="text" 
                  name="address"
                  className="form-input"
                  placeholder="ÊÇ®ÁöÑÂ∏∏‰ΩèÂú∞ÂùÄ"
                  value={formData.address}
                  onChange={handleChange}
                />
              </div>

              {/* ÁÆ°ÁêÜÂëòÈ™åËØÅÁ†Å */}


              <div style={{ display: 'flex', gap: '1rem' }}>
                <button type="button" className="btn btn-secondary" style={{ flex: 1 }} onClick={prevStep}>
                  ‚Üê ‰∏ä‰∏ÄÊ≠•
                </button>
                <button type="button" className="btn btn-primary" style={{ flex: 1 }} onClick={nextStep}>
                  ‰∏ã‰∏ÄÊ≠• ‚Üí
                </button>
              </div>
            </>
          )}

          {/* Á¨¨‰∏âÊ≠•ÔºöËßíËâ≤ÁâπÂÆö‰ø°ÊÅØ */}
          {step === 3 && (
            <>
              {/* PINÁî®Êà∑ÁâπÂÆö‰ø°ÊÅØ */}
              {formData.userType === 'pin' && (
                <>
                  <div className="form-group">
                    <label className="form-label">Âá∫ÁîüÊó•Êúü</label>
                    <input 
                      type="date" 
                      name="birthDate"
                      className="form-input"
                      value={formData.birthDate}
                      onChange={handleChange}
                    />
                  </div>

                  <div className="form-group">
                    <label className="form-label">Á¥ßÊÄ•ËÅîÁ≥ª‰∫∫</label>
                    <input 
                      type="text" 
                      name="emergencyContact"
                      className="form-input"
                      placeholder="Á¥ßÊÄ•ËÅîÁ≥ª‰∫∫ÂßìÂêçÂíåÁîµËØù"
                      value={formData.emergencyContact}
                      onChange={handleChange}
                    />
                  </div>
                </>
              )}

              {/* CSRÁî®Êà∑ÁâπÂÆö‰ø°ÊÅØ */}
              {formData.userType === 'csr' && (
                <>
                  <div className="form-group">
                    <label className="form-label">ÊâÄÂ±ûÊú∫ÊûÑ</label>
                    <input 
                      type="text" 
                      name="organization"
                      className="form-input"
                      placeholder="ÂÖ¨Âè∏ÊàñÁªÑÁªáÂêçÁß∞"
                      value={formData.organization}
                      onChange={handleChange}
                    />
                  </div>

                  <div className="form-group">
                    <label className="form-label">ÊäÄËÉΩ‰∏ìÈïø</label>
                    <div className="form-checkbox-group">
                      {[
                        { value: 'medical', label: 'ÂåªÁñóÊä§ÁêÜ' },
                        { value: 'transport', label: '‰∫§ÈÄöÂá∫Ë°å' },
                        { value: 'technology', label: 'ÊäÄÊúØÊîØÊåÅ' },
                        { value: 'education', label: 'ÊïôËÇ≤ÂüπËÆ≠' },
                        { value: 'household', label: 'ÂÆ∂Âä°ÊúçÂä°' },
                        { value: 'companion', label: 'Èô™‰º¥ÂÖ≥ÊÄÄ' }
                      ].map(skill => (
                        <label key={skill.value} className="form-checkbox">
                          <input
                            type="checkbox"
                            name="skills"
                            value={skill.value}
                            checked={formData.skills.includes(skill.value)}
                            onChange={handleChange}
                          />
                          <span>{skill.label}</span>
                        </label>
                      ))}
                    </div>
                  </div>
                </>
              )}



              <div style={{ display: 'flex', gap: '1rem' }}>
                <button type="button" className="btn btn-secondary" style={{ flex: 1 }} onClick={prevStep}>
                  ‚Üê ‰∏ä‰∏ÄÊ≠•
                </button>
                <button 
                  type="submit" 
                  className="btn btn-primary"
                  style={{ flex: 1 }}
                  disabled={isLoading}
                >
                  {isLoading && <span className="loading-spinner"></span>}
                  {isLoading ? 'Ê≥®ÂÜå‰∏≠...' : 'ÂÆåÊàêÊ≥®ÂÜå'}
                </button>
              </div>
            </>
          )}
        </form>

        <div className="auth-links">
          <p className="auth-link">
            Â∑≤ÊúâË¥¶Âè∑Ôºü <Link to="/login">Á´ãÂç≥ÁôªÂΩï</Link>
          </p>
        </div>
      </div>
    </div>
  );
};

export default Register;
