// src/context/AuthContext.jsx
import React, { createContext, useContext, useState, useEffect } from 'react';
import { AUTH_CONFIG, isAdminEmail } from '../config/auth';
import apiService from '../services/apiService';

const AuthContext = createContext();

export const useAuth = () => {
  const context = useContext(AuthContext);
  if (!context) {
    throw new Error('useAuth must be used within an AuthProvider');
  }
  return context;
};

export const AuthProvider = ({ children }) => {
  const [user, setUser] = useState(null);
  const [loading, setLoading] = useState(true);
  
  useEffect(() => {
    // 检查本地存储中是否有用户信息
    const savedUser = localStorage.getItem('user');
    if (savedUser) {
      setUser(JSON.parse(savedUser));
    }
    setLoading(false);
  }, []);
  
  const login = async (email, password, userType) => {
    try {
      setLoading(true);
      // 模拟API调用
      await new Promise(resolve => setTimeout(resolve, 1000));
      
      // 检查是否是管理员邮箱
      const isAdmin = isAdminEmail(email);
      let finalUserType = userType;
      
      if (isAdmin) {
        finalUserType = 'admin';
      }
      
      // 检查用户是否已被删除
      const deletedUsers = JSON.parse(localStorage.getItem('deletedUsers') || '[]');
      const isDeleted = deletedUsers.some(u => u.email === email);
      if (isDeleted) {
        return { success: false, errorCode: 'USER_NOT_EXISTS' };
      }
      
      // 检查是否是已注册用户
      const registeredUsers = JSON.parse(localStorage.getItem('registeredUsers') || '[]');
      const existingUser = registeredUsers.find(u => u.email === email && u.userType === finalUserType);
      
      let userData;
      if (existingUser) {
        // 检查用户状态
        if (existingUser.status === 'suspended') {
          return { success: false, errorCode: 'USER_SUSPENDED' };
        }
        if (existingUser.status === 'deleted') {
          return { success: false, errorCode: 'USER_NOT_EXISTS' };
        }
        
        // 关键修复：验证密码
        if (existingUser.password && existingUser.password !== password) {
          return { success: false, errorCode: 'INVALID_CREDENTIALS', error: '邮箱或密码错误' };
        }
        
        // 使用注册时的完整信息
        userData = existingUser;
      } else {
        // 只允许管理员或已注册用户
        if (!isAdmin) {
          return { success: false, errorCode: 'USER_NOT_EXISTS' };
        }
        
        // 对于管理员账户，设置默认密码验证
        if (password !== AUTH_CONFIG.DEFAULT_ADMIN_PASSWORD) {
          return { success: false, errorCode: 'INVALID_CREDENTIALS', error: '邮箱或密码错误' };
        }
        
        // 创建管理员数据
        userData = {
          id: 'admin_' + Date.now(),
          email,
          userType: finalUserType,
          name: email.split('@')[0],
          status: 'active',
          password: AUTH_CONFIG.DEFAULT_ADMIN_PASSWORD // 存储密码以便后续验证
        };
      }
      
      setUser(userData);
      localStorage.setItem('user', JSON.stringify(userData));
      return { success: true };
    } catch (error) {
      return { success: false, error: error.message };
    } finally {
      setLoading(false);
    }
  };
  
  const register = async (formData) => {
    try {
      setLoading(true);
      
      // 使用后端API注册用户
      const response = await apiService.register({
        name: formData.name,
        email: formData.email,
        password: formData.password,
        userType: formData.userType,
        phone: formData.phone,
        address: formData.address,
        organization: formData.organization,
        skills: formData.skills,
        birthDate: formData.birthDate,
        emergencyContact: formData.emergencyContact
      });
      
      // 注册成功后自动登录
      if (response.user && response.token) {
        // 存储用户信息和token
        const userData = {
          id: response.user._id,
          name: response.user.name,
          email: response.user.email,
          userType: response.user.userType,
          phone: response.user.phone,
          address: response.user.address,
          organization: response.user.organization,
          skills: response.user.skills,
          status: response.user.status
        };
        
        setUser(userData);
        localStorage.setItem('user', JSON.stringify(userData));
        localStorage.setItem('token', response.token);
        
        return { success: true };
      } else {
        return { success: false, error: 'Registration failed' };
      }
    } catch (error) {
      console.error('Registration error:', error);
      if (error.message.includes('User already exists')) {
        return { success: false, errorCode: 'EMAIL_ALREADY_EXISTS' };
      }
      return { success: false, error: error.message };
    } finally {
      setLoading(false);
    }
  };
  
  const logout = () => {
    setUser(null);
    localStorage.removeItem('user');
  };
  
  return (
    <AuthContext.Provider value={{ user, login, register, logout, loading }}>
      {children}
    </AuthContext.Provider>
  );
};
