// Dashboard.jsx (PIN Dashboard)
import React, { useState } from 'react';
import { useAuth } from '../../context/AuthContext';
import Navbar from '../../components/Layout/Navbar';
import CreateRequestForm from '../../components/PIN/CreateRequestForm';

const PINDashboard = () => {
  const { user } = useAuth();
  const [showCreateForm, setShowCreateForm] = useState(false);
  
  const requests = [
    { 
      id: 1, 
      title: 'åŒ»é™¢å¤è¯Šé™ªåŒ', 
      category: 'medical', 
      status: 'matched', 
      viewCount: 24, 
      shortlistCount: 3,
      createdAt: '2024-01-15',
      volunteer: 'å¼ å¿—æ„¿è€…'
    },
    { 
      id: 2, 
      title: 'è¶…å¸‚è´­ç‰©å¸®åŠ©', 
      category: 'shopping', 
      status: 'pending', 
      viewCount: 12, 
      shortlistCount: 1,
      createdAt: '2024-01-18'
    }
  ];

  const stats = [
    { title: 'æ€»è¯·æ±‚æ•°', value: requests.length, icon: 'ğŸ“‹', color: 'primary' },
    { title: 'å·²åŒ¹é…', value: requests.filter(r => r.status === 'matched').length, icon: 'âœ…', color: 'success' },
    { title: 'ç­‰å¾…åŒ¹é…', value: requests.filter(r => r.status === 'pending').length, icon: 'â³', color: 'warning' },
    { title: 'æ€»æµè§ˆé‡', value: requests.reduce((sum, r) => sum + r.viewCount, 0), icon: 'ğŸ‘€', color: 'info' }
  ];

  return (
    <div className="page-container">
      <Navbar userType="pin" user={user} />
      <div className="main-content">
        <div className="dashboard">
          <div className="dashboard-header">
            <div className="header-content">
              <h1 className="dashboard-title">æˆ‘çš„ä»ªè¡¨æ¿</h1>
              <p className="dashboard-subtitle">æ¬¢è¿å›æ¥ï¼Œ{user?.name}ï¼</p>
            </div>
            <button 
              className="btn btn-primary"
              onClick={() => setShowCreateForm(true)}
            >
              <span className="btn-icon">+</span>
              åˆ›å»ºæ–°è¯·æ±‚
            </button>
          </div>
          
          <div className="stats-grid">
            {stats.map((stat, index) => (
              <div key={index} className={`stat-card ${stat.color}`}>
                <div className="stat-header">
                  <div className="stat-info">
                    <div className="stat-title">{stat.title}</div>
                    <div className="stat-value">{stat.value}</div>
                  </div>
                  <div className={`stat-icon ${stat.color}`}>
                    {stat.icon}
                  </div>
                </div>
              </div>
            ))}
          </div>
          
          <div className="content-grid">
            <div className="card">
              <div className="card-header">
                <h3 className="card-title">æˆ‘çš„è¯·æ±‚åˆ—è¡¨</h3>
                <p className="card-subtitle">ç®¡ç†æ‚¨çš„æ‰€æœ‰æ±‚åŠ©è¯·æ±‚</p>
              </div>
              <div className="card-body">
                {requests.length > 0 ? (
                  <div className="list">
                    {requests.map(request => (
                      <div key={request.id} className="list-item">
                        <div className="list-item-header">
                          <div className="list-item-title">{request.title}</div>
                          <div className={`status-badge ${request.status === 'matched' ? 'success' : 'warning'}`}>
                            {request.status === 'matched' ? 'å·²åŒ¹é…' : 'ç­‰å¾…åŒ¹é…'}
                          </div>
                        </div>
                        <div className="list-item-content">
                          <div style={{ display: 'flex', gap: '1rem', flexWrap: 'wrap', marginBottom: '0.5rem' }}>
                            <span className="opportunity-tag">{getCategoryName(request.category)}</span>
                            <span style={{ color: 'var(--text-muted)', fontSize: 'var(--text-sm)' }}>
                              ğŸ‘ï¸ {request.viewCount} æ¬¡æŸ¥çœ‹
                            </span>
                            <span style={{ color: 'var(--text-muted)', fontSize: 'var(--text-sm)' }}>
                              â­ {request.shortlistCount} æ¬¡æ”¶è—
                            </span>
                          </div>
                          {request.requesterName && (
                            <div style={{ marginBottom: '0.5rem', padding: '0.5rem', backgroundColor: 'var(--bg-secondary)', borderRadius: '0.375rem' }}>
                              <div style={{ fontSize: 'var(--text-sm)', color: 'var(--text-muted)' }}>
                                <strong>è¯·æ±‚äººï¼š</strong> {request.requesterName}
                                {request.requesterEmail && (
                                  <span style={{ marginLeft: '1rem' }}>ğŸ“§ {request.requesterEmail}</span>
                                )}
                              </div>
                              {request.requesterPhone && (
                                <div style={{ fontSize: 'var(--text-sm)', color: 'var(--text-muted)', marginTop: '0.25rem' }}>
