// Dashboard_new.jsx - ÈáçÊñ∞ËÆæËÆ°ÁöÑÁé∞‰ª£ÂåñÁÆ°ÁêÜÂëò‰ª™Ë°®Êùø
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { useAuth } from '../../context/AuthContext';
import { useLanguage } from '../../context/LanguageContext';
import { DataService } from '../../services/dataService';
import Navbar from '../../components/Layout/Navbar';
import { isSystemAdmin, isPlatformManager } from '../../utils/permissions';
import '../../styles/modern-dashboard.css';

const AdminDashboard = () => {
  const { user } = useAuth();
  const { t } = useLanguage();
  const [dashboardData, setDashboardData] = useState({
    stats: [],
    activities: []
  });
  const [loading, setLoading] = useState(true);
  const [lastUpdated, setLastUpdated] = useState(null);
  
  useEffect(() => {
    // Âä†ËΩΩ‰ª™Ë°®ÊùøÊï∞ÊçÆ
    const loadDashboardData = async () => {
      try {
        // Ê∏ÖÁ©∫ÁºìÂ≠òÔºåÁ°Æ‰øùËé∑ÂèñÊúÄÊñ∞Êï∞ÊçÆ
        DataService.clearCache();
        
        // ÂàùÂßãÂåñÁ§∫‰æãÊï∞ÊçÆ
        await DataService.initializeData();
        
        const statistics = await DataService.getStatistics();
        const users = await DataService.getUsers();
        const requests = await DataService.getRequests();
        
        // ËÆ°ÁÆóÊú¨ÊúàÊñ∞Â¢û
        const currentMonth = new Date().getMonth();
        const currentYear = new Date().getFullYear();
        
        const thisMonthUsers = (users || []).filter(user => {
          const userDate = new Date(user.registeredAt || user.createdAt || Date.now());
          return userDate.getMonth() === currentMonth && userDate.getFullYear() === currentYear;
        });
        
        const thisMonthRequests = (requests || []).filter(request => {
          const requestDate = new Date(request.createdAt || Date.now());
          return requestDate.getMonth() === currentMonth && requestDate.getFullYear() === currentYear;
        });
        
        // ‰ªäÊó•ÂåπÈÖç
        const today = new Date();
        today.setHours(0, 0, 0, 0);
        const todayMatched = (requests || []).filter(request => {
          if (request.status !== 'matched') return false;
          const matchDate = new Date(request.matchedAt || request.updatedAt || request.createdAt);
          matchDate.setHours(0, 0, 0, 0);
          return matchDate.getTime() === today.getTime();
        });
        
        const pendingRequests = (requests || []).filter(r => r.status === 'pending');
        
        // Ê†πÊçÆÁÆ°ÁêÜÂëòÁ±ªÂûãÁîüÊàêÁªüËÆ°Âç°Áâá
        const allStats = [
          { 
            title: t('admin.dashboard.totalUsers'), 
            value: (users || []).length.toString(), 
            change: `+${thisMonthUsers.length}`, 
            trend: thisMonthUsers.length > 0 ? 'up' : 'neutral', 
            icon: 'üë•', 
            color: 'primary',
            permission: 'system_admin' // Âè™Êúâ System Admin ÂèØËßÅ
          },
          { 
            title: t('admin.dashboard.activeRequests'), 
            value: (requests || []).filter(r => r.status === 'pending' || r.status === 'matched').length.toString(), 
            change: `+${thisMonthRequests.length}`, 
            trend: thisMonthRequests.length > 0 ? 'up' : 'neutral', 
            icon: 'üìã', 
            color: 'secondary',
            permission: 'both' // ‰∏§ÁßçÁÆ°ÁêÜÂëòÈÉΩÂèØËßÅ
          },
          { 
            title: t('admin.dashboard.todayMatches'), 
            value: todayMatched.length.toString(), 
            change: `+${todayMatched.length}`, 
            trend: todayMatched.length > 0 ? 'up' : 'neutral', 
            icon: '‚úÖ', 
            color: 'success',
            permission: 'platform_manager' // Âè™Êúâ Platform Manager ÂèØËßÅ
          },
          { 
            title: t('admin.dashboard.pendingReview'), 
            value: pendingRequests.length.toString(), 
            change: `${pendingRequests.length > 5 ? '+' : ''}${pendingRequests.length}`, 
            trend: pendingRequests.length > 5 ? 'up' : pendingRequests.length > 0 ? 'neutral' : 'down', 
            icon: '‚è≥', 
            color: 'warning',
            permission: 'both' // ‰∏§ÁßçÁÆ°ÁêÜÂëòÈÉΩÂèØËßÅ
          }
        ];
        
        // Ê†πÊçÆÁî®Êà∑ÊùÉÈôêËøáÊª§ÁªüËÆ°Âç°Áâá
        const stats = allStats.filter(stat => {
          if (stat.permission === 'both') return true;
          if (stat.permission === 'system_admin') return isSystemAdmin(user);
          if (stat.permission === 'platform_manager') return isPlatformManager(user);
          return false;
        });
        
        // ÊúÄËøëÊ¥ªÂä® - Ê†πÊçÆÁÆ°ÁêÜÂëòÁ±ªÂûãÊòæÁ§∫‰∏çÂêåÂÜÖÂÆπ
        const activities = [];
        
        // System Admin ÊòæÁ§∫Êñ∞Áî®Êà∑Ê¥ªÂä®
        if (isSystemAdmin(user)) {
          const recentUsers = (users || [])
            .sort((a, b) => new Date(b.registeredAt || b.createdAt || 0) - new Date(a.registeredAt || a.createdAt || 0))
            .slice(0, 3);
          
          recentUsers.forEach(user => {
            activities.push({
              icon: 'üë§',
              content: `${t('common.newUser')} - ${user.name}`,
              time: DataService.getTimeAgo(user.registeredAt || user.createdAt, t)
            });
          });
        }
        
        // Platform Manager ÊòæÁ§∫ËØ∑Ê±ÇÂíåÂåπÈÖçÊ¥ªÂä®
        if (isPlatformManager(user)) {
          const recentRequests = requests
            .sort((a, b) => new Date(b.createdAt || 0) - new Date(a.createdAt || 0))
            .slice(0, 5);
          
          // Ëé∑ÂèñÊâÄÊúâÂàÜÁ±ª‰ª•‰æøÂêéÁª≠Êü•Êâæ
          const allCategories = await DataService.getCategories();
          
          recentRequests.forEach(request => {
            const icon = request.status === 'matched' ? '‚úÖ' : 'üìã';
            const category = (allCategories || []).find(cat => cat.id === request.category);
            const categoryName = category ? t(category.name) : request.category;
            const content = request.status === 'matched' 
              ? `${categoryName}${t('common.requestMatched')}`
              : `${t('common.newRequest')} - ${categoryName}`;
            
            activities.push({
              icon,
              content,
              time: DataService.getTimeAgo(request.createdAt, t)
            });
          });
        }
        
        setDashboardData({ stats, activities });
        setLastUpdated(new Date());
      } catch (error) {
        console.error('Âä†ËΩΩ‰ª™Ë°®ÊùøÊï∞ÊçÆÂ§±Ë¥•:', error);
        setDashboardData({
          stats: [
            { title: t('admin.dashboard.totalUsers'), value: '0', change: '+0', trend: 'neutral', icon: 'üë•', color: 'primary' },
            { title: t('admin.dashboard.activeRequests'), value: '0', change: '+0', trend: 'neutral', icon: 'üìã', color: 'secondary' },
            { title: t('admin.dashboard.todayMatches'), value: '0', change: '+0', trend: 'neutral', icon: '‚úÖ', color: 'success' },
            { title: t('admin.dashboard.pendingReview'), value: '0', change: '+0', trend: 'neutral', icon: '‚è≥', color: 'warning' }
          ],
          activities: []
        });
      } finally {
        setLoading(false);
      }
    };
    
    loadDashboardData();
    
    // ÂÆöÊó∂Âà∑Êñ∞
    const interval = setInterval(loadDashboardData, 30000);
    return () => clearInterval(interval);
  }, [t]);

  // Âø´Êç∑ÂäüËÉΩÂç°Áâá - Ê†πÊçÆÁÆ°ÁêÜÂëòÁ±ªÂûãÊòæÁ§∫‰∏çÂêåÁöÑÂäüËÉΩ
  const getQuickActions = () => {
    if (isSystemAdmin(user)) {
      // System Admin ‰∏ìÂ±ûÂäüËÉΩ
      return [
        { title: t('admin.dashboard.userManagement') || 'User Management', icon: 'üë•', path: '/admin/users', color: 'blue', description: 'Manage all users (#50)' },
        { title: t('admin.dashboard.systemLogs') || 'System Logs', icon: 'üìã', path: '/admin/system-logs', color: 'orange', description: 'Monitor login activity (#51)' },
        { title: t('admin.dashboard.systemSettings') || 'System Settings', icon: '‚öôÔ∏è', path: '/admin/settings', color: 'gray', description: 'Configure system (#56)' },
        { title: t('admin.dashboard.alerts') || 'Alerts', icon: 'üîî', path: '/admin/alerts', color: 'red', description: 'System alerts' }
      ];
    } else if (isPlatformManager(user)) {
      // Platform Manager ‰∏ìÂ±ûÂäüËÉΩ
      return [
        { title: t('admin.dashboard.serviceCategories') || 'Service Categories', icon: 'üìÅ', path: '/admin/categories', color: 'green', description: 'Manage categories (#57)' },
        { title: t('admin.dashboard.dataReports') || 'Reports', icon: 'üìä', path: '/admin/reports', color: 'purple', description: 'Generate reports (#58)' },
        { title: t('admin.dashboard.participation') || 'Participation', icon: 'üìà', path: '/admin/participation', color: 'blue', description: 'Monitor engagement (#59)' },
        { title: t('admin.dashboard.csrPerformance') || 'CSR Performance', icon: '‚≠ê', path: '/admin/performance', color: 'yellow', description: 'Track CSR (#62)' }
      ];
    }
    
    return [];
  };

  const quickActions = getQuickActions();

  return (
    <div className="modern-admin-container">
      <Navbar userType="admin" user={user} />
      
      <div className="modern-main-content">
        {/* Áé∞‰ª£ÂåñÈ°µÈù¢Ê†áÈ¢ò */}
        <div className="modern-dashboard-header">
          <div className="header-background">
            <div className="header-content">
              <div className="header-main">
                <div className="header-avatar">
                  {user?.avatar ? (
                    <img src={user.avatar} alt="User Avatar" className="avatar-image" />
                  ) : (
                    <div className="avatar-icon">{user?.name?.charAt(0)?.toUpperCase() || 'üë®‚Äçüíº'}</div>
                  )}
                  <div className="avatar-status"></div>
                </div>
                <div className="header-text">
                  <h1 className="modern-dashboard-title">
                    {t('admin.dashboard.title')}
                  </h1>
                  <p className="welcome-text">
                    {t('admin.dashboard.welcome') || 'Ê¨¢ËøéÂõûÊù•'}, <span className="user-name">{user?.name}</span>
                  </p>
                  <div className="dashboard-subtitle">
                    {t('admin.dashboard.description') || 'Á≥ªÁªüÁÆ°ÁêÜ‰∏éÊï∞ÊçÆÁõëÊéß‰∏≠ÂøÉ'}
                  </div>
                </div>
              </div>
              
              <div className="header-status">
                {lastUpdated && (
                  <div className="update-indicator">
                    <span className="update-icon">üîÑ</span>
                    <span className="update-text">
                      {t('time.updatedAt') || 'Êõ¥Êñ∞‰∫é'}: {lastUpdated.toLocaleTimeString()}
                    </span>
                  </div>
                )}
              </div>
            </div>
          </div>
        </div>

        {/* Áé∞‰ª£ÂåñÊìç‰ΩúÊåâÈíÆÂå∫Âüü */}
        <div className="dashboard-actions-card">
          <div className="actions-container">
            <button 
              className="modern-action-btn refresh-btn"
              onClick={() => {
                setLoading(true);
                setTimeout(() => {
                  window.location.reload();
                }, 500);
              }}
              disabled={loading}
            >
              <span className="btn-icon">{loading ? '‚è≥' : 'üîÑ'}</span>
              <span className="btn-text">
                {loading ? (t('common.refreshing') || 'Âà∑Êñ∞‰∏≠') : (t('admin.dashboard.refreshData') || 'Âà∑Êñ∞Êï∞ÊçÆ')}
              </span>
            </button>
            
            {/* ÂØºÂá∫Êä•ÂëäÊåâÈíÆ - Âè™Êúâ Platform Manager ÂèØËßÅ */}
            {isPlatformManager(user) && (
              <button 
                className="modern-action-btn export-btn"
                onClick={async () => {
                  try {
                    const statistics = await DataService.getStatistics();
                    const users = await DataService.getUsers();
                    const requests = await DataService.getRequests();
                    
                    const report = {
                      title: 'System Report',
                      generatedAt: new Date().toISOString(),
                      statistics,
                      summary: {
                        totalUsers: (users || []).length,
                        totalRequests: (requests || []).length,
                        activeRequests: (requests || []).filter(r => r.status === 'pending' || r.status === 'matched').length,
                        matchedRequests: (requests || []).filter(r => r.status === 'matched').length
                      }
                    };
                    
                    const dataStr = JSON.stringify(report, null, 2);
                    const dataBlob = new Blob([dataStr], { type: 'application/json' });
                    const link = document.createElement('a');
                    link.href = URL.createObjectURL(dataBlob);
                    link.download = `system_report_${new Date().toISOString().split('T')[0]}.json`;
                    document.body.appendChild(link);
                    link.click();
                    document.body.removeChild(link);
                    
                    alert(t('admin.dashboard.reportGenerated') || 'Êä•ÂëäÂ∑≤ÁîüÊàê');
                  } catch (error) {
                    console.error('ÁîüÊàêÊä•ÂëäÂ§±Ë¥•:', error);
                    alert(t('admin.dashboard.reportError') || 'ÁîüÊàêÊä•ÂëäÂ§±Ë¥•');
                  }
                }}
              >
                <span className="btn-icon">üìä</span>
                <span className="btn-text">{t('admin.dashboard.exportReport') || 'ÂØºÂá∫Êä•Âëä'}</span>
              </button>
            )}
          </div>
        </div>

        {/* Áé∞‰ª£ÂåñÁªüËÆ°Âç°Áâá */}
        <div className="modern-stats-section">
          <div className="stats-grid">
            {loading ? (
              // Âä†ËΩΩÁä∂ÊÄÅ
              Array.from({ length: 4 }).map((_, index) => (
                <div key={index} className="modern-stat-card loading">
                  <div className="stat-content">
                    <div className="stat-icon loading-icon">‚è≥</div>
                    <div className="stat-info">
                      <div className="stat-title">{t('common.loading') || 'Âä†ËΩΩ‰∏≠'}</div>
                      <div className="stat-value">--</div>
                      <div className="stat-change">-- --</div>
                    </div>
                  </div>
                </div>
              ))
            ) : (
              dashboardData.stats.map((stat, index) => (
                <div key={index} className={`modern-stat-card ${stat.color}`}>
                  <div className="stat-content">
                    <div className={`stat-icon ${stat.color}`}>
                      {stat.icon}
                    </div>
                    <div className="stat-info">
                      <div className="stat-title">{stat.title}</div>
                      <div className="stat-value">{stat.value}</div>
                      <div className={`stat-change ${stat.trend}`}>
                        {stat.trend === 'up' ? '‚Üó' : stat.trend === 'down' ? '‚Üò' : '‚Üí'} 
                        {stat.change} {t('admin.dashboard.thisMonth') || 'Êú¨Êúà'}
                      </div>
                    </div>
                  </div>
                </div>
              ))
            )}
          </div>
        </div>

        {/* Áé∞‰ª£ÂåñÂÜÖÂÆπÁΩëÊ†º */}
        <div className="modern-content-section">
          <div className="content-grid">
            {/* Âø´ÈÄüÊìç‰ΩúÂç°Áâá */}
            <div className="modern-card">
              <div className="card-header">
                <div className="card-title-section">
                  <span className="card-icon">‚ö°</span>
                  <h3 className="card-title">{t('admin.dashboard.quickActions') || 'Âø´ÈÄüÊìç‰Ωú'}</h3>
                </div>
              </div>
              <div className="card-body">
                <div className="modern-actions-grid">
                  {quickActions.map((action, index) => (
                    <Link key={index} to={action.path} className={`modern-action-card ${action.color}`}>
                      <div className="action-content">
                        <div className={`action-icon ${action.color}`}>
                          {action.icon}
                        </div>
                        <div className="action-title">{action.title}</div>
                        <div className="action-arrow">‚Üí</div>
                      </div>
                    </Link>
                  ))}
                </div>
              </div>
            </div>

            {/* ÊúÄËøëÊ¥ªÂä®Âç°Áâá */}
            <div className="modern-card">
              <div className="card-header">
                <div className="card-title-section">
                  <span className="card-icon">üìà</span>
                  <h3 className="card-title">{t('admin.dashboard.recentActivity') || 'ÊúÄËøëÊ¥ªÂä®'}</h3>
                </div>
              </div>
              <div className="card-body">
                {loading ? (
                  <div className="activity-loading">
                    <div className="loading-spinner">‚è≥</div>
                    <p>{t('common.loading') || 'Âä†ËΩΩ‰∏≠'}</p>
                  </div>
                ) : dashboardData.activities.length > 0 ? (
                  <div className="modern-activity-list">
                    {dashboardData.activities.map((activity, index) => (
                      <div key={index} className="modern-activity-item">
                        <div className="activity-icon">{activity.icon}</div>
                        <div className="activity-content">
                          <p className="activity-text">{activity.content}</p>
                          <span className="activity-time">{activity.time}</span>
                        </div>
                      </div>
                    ))}
                  </div>
                ) : (
                  <div className="no-activity">
                    <div className="no-activity-icon">üìä</div>
                    <p>{t('admin.dashboard.noRecentActivity') || 'ÊöÇÊó†ÊúÄËøëÊ¥ªÂä®'}</p>
                  </div>
                )}
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default AdminDashboard;
