// Dashboard.jsx (PIN Dashboard)
import React, { useState, useEffect } from 'react';
import { useAuth } from '../../context/AuthContext';
import Navbar from '../../components/Layout/Navbar';
import CreateRequestForm from '../../components/PIN/CreateRequestForm';

const PINDashboard = () => {
  const { user } = useAuth();
  const [showCreateForm, setShowCreateForm] = useState(false);
  const [showRequestDetail, setShowRequestDetail] = useState(null);
  const [editingRequest, setEditingRequest] = useState(null);
  
  // ä»localStorageè·å–å½“å‰ç”¨æˆ·çš„ä¸“å±è¯·æ±‚
  const getUserRequests = () => {
    if (!user?.id) return [];
    const allRequests = JSON.parse(localStorage.getItem('userRequests') || '[]');
    return allRequests.filter(request => request.requesterId === user.id);
  };
  
  const [requests, setRequests] = useState(getUserRequests());

  // å½“ç”¨æˆ·å˜åŒ–æ—¶é‡æ–°åŠ è½½è¯·æ±‚
  useEffect(() => {
    setRequests(getUserRequests());
  }, [user?.id]);

  const stats = [
    { title: 'æ€»è¯·æ±‚æ•°', value: requests.length, icon: 'ğŸ“‹', color: 'primary' },
    { title: 'å·²åŒ¹é…', value: requests.filter(r => r.status === 'matched').length, icon: 'âœ…', color: 'success' },
    { title: 'ç­‰å¾…åŒ¹é…', value: requests.filter(r => r.status === 'pending').length, icon: 'â³', color: 'warning' },
    { title: 'æ€»æµè§ˆé‡', value: requests.reduce((sum, r) => sum + r.viewCount, 0), icon: 'ğŸ‘€', color: 'info' }
  ];

  // å¤„ç†åˆ›å»ºæ–°è¯·æ±‚
  const handleCreateRequest = (newRequest) => {
    const requestWithUser = {
      ...newRequest,
      requesterId: user.id,
      requesterName: user.name,
      requesterEmail: user.email,
      requesterPhone: user.phone || '',
      requesterAddress: user.address || newRequest.location,
    };
    
    // æ›´æ–°æœ¬åœ°çŠ¶æ€
    setRequests(prev => [requestWithUser, ...prev]);
    
    // ä¿å­˜åˆ°localStorage
    const allRequests = JSON.parse(localStorage.getItem('userRequests') || '[]');
    const updatedRequests = [requestWithUser, ...allRequests];
    localStorage.setItem('userRequests', JSON.stringify(updatedRequests));
  };

  // å¤„ç†æŸ¥çœ‹è¯·æ±‚è¯¦æƒ…
  const handleViewDetail = (request) => {
    setShowRequestDetail(request);
  };

  // å¤„ç†ç¼–è¾‘è¯·æ±‚
  const handleEditRequest = (request) => {
    setEditingRequest(request);
    setShowCreateForm(true); // å¤ç”¨åˆ›å»ºè¡¨å•è¿›è¡Œç¼–è¾‘
  };

  // å¤„ç†æ›´æ–°è¯·æ±‚
  const handleUpdateRequest = (updatedRequest) => {
    // æ›´æ–°æœ¬åœ°çŠ¶æ€
    setRequests(prev => prev.map(req => 
      req.id === updatedRequest.id ? { ...updatedRequest, requesterId: user.id } : req
