// Dashboard.jsx (PIN Dashboard)
import React, { useState, useEffect } from 'react';
import { useAuth } from '../../context/AuthContext';
import { useLanguage } from '../../context/LanguageContext';
import { DataService } from '../../services/dataService';
import Navbar from '../../components/Layout/Navbar';
import CreateRequestForm from '../../components/PIN/CreateRequestForm';
import RequestDetailModal from '../../components/PIN/RequestDetailModal';

const PINDashboard = () => {
  const { user } = useAuth();
  const { t } = useLanguage();
  const [showCreateForm, setShowCreateForm] = useState(false);
  const [showRequestDetail, setShowRequestDetail] = useState(null);
  const [editingRequest, setEditingRequest] = useState(null);
  
  const [requests, setRequests] = useState([]);

  // ÂàùÂßãÂåñÊï∞ÊçÆÂíåÂä†ËΩΩÁî®Êà∑ËØ∑Ê±Ç
  useEffect(() => {
    const loadUserData = async () => {
      try {
        await DataService.initializeData();
        if (user?.id) {
          const userRequests = await DataService.getUserRequests(user.id);
          setRequests(userRequests || []); // Á°Æ‰øùÊòØÊï∞ÁªÑ
        }
      } catch (error) {
        console.error('Âä†ËΩΩÁî®Êà∑Êï∞ÊçÆÂ§±Ë¥•:', error);
        setRequests([]); // ËÆæÁΩÆ‰∏∫Á©∫Êï∞ÁªÑÈò≤Ê≠¢ÈîôËØØ
      }
    };
    
    loadUserData();
  }, [user?.id]);

  // ‰ΩøÁî®Êï∞ÊçÆÊúçÂä°Ëé∑ÂèñÁªüËÆ°‰ø°ÊÅØ
  const stats = [
    { title: t('stats.totalRequests'), value: requests.length, icon: 'üìã', color: 'primary' },
    { title: t('stats.matched'), value: requests.filter(r => r.status === 'matched').length, icon: '‚úÖ', color: 'success' },
    { title: t('stats.pending'), value: requests.filter(r => r.status === 'pending').length, icon: '‚è≥', color: 'warning' },
    { title: t('stats.totalViews'), value: requests.reduce((sum, r) => sum + (r.viewCount || 0), 0), icon: 'üëÄ', color: 'info' }
  ];

  // Â§ÑÁêÜÂàõÂª∫Êñ∞ËØ∑Ê±Ç
  const handleCreateRequest = async (newRequest) => {
    try {
      const requestWithUser = {
        ...newRequest,
        // ‰øÆÊ≠£locationÊ†ºÂºè‰∏∫ÂêéÁ´ØÊúüÊúõÁöÑÁªìÊûÑ
        location: {
          address: newRequest.location
        },
        // Á°Æ‰øùÊó•ÊúüÊ†ºÂºèÊ≠£Á°Æ
        expectedDate: newRequest.expectedDate ? new Date(newRequest.expectedDate).toISOString() : null,
        // Á°Æ‰øùÂøóÊÑøËÄÖÊï∞ÈáèÊòØÊï∞Â≠óÁ±ªÂûã
        volunteersNeeded: parseInt(newRequest.volunteersNeeded) || 1,
        requesterId: user.id,
        requesterName: user.name,
        requesterEmail: user.email,
        requesterPhone: user.phone || '',
        requesterAddress: user.address || newRequest.location,
      };
      
      // ‰ΩøÁî®Êï∞ÊçÆÊúçÂä°ÂàõÂª∫ËØ∑Ê±Ç
      const createdRequest = await DataService.createRequest(requestWithUser);
      
      // Êõ¥Êñ∞Êú¨Âú∞Áä∂ÊÄÅ
      setRequests(prev => [createdRequest, ...prev]);
      
    } catch (error) {
      console.error('ÂàõÂª∫ËØ∑Ê±ÇÂ§±Ë¥•:', error);
      alert(t('error.createRequestFailed') || 'ÂàõÂª∫ËØ∑Ê±ÇÂ§±Ë¥•');
    }
  };

  // Â§ÑÁêÜÊü•ÁúãËØ∑Ê±ÇËØ¶ÊÉÖ
  const handleViewDetail = (request) => {
    setShowRequestDetail(request);
  };

  // Â§ÑÁêÜÁºñËæëËØ∑Ê±Ç
  const handleEditRequest = (request) => {
    setEditingRequest(request);
    setShowCreateForm(true); // Â§çÁî®ÂàõÂª∫Ë°®ÂçïËøõË°åÁºñËæë
  };

  // Â§ÑÁêÜÊõ¥Êñ∞ËØ∑Ê±Ç
  const handleUpdateRequest = async (updatedRequest) => {
    try {
      const requestWithUser = { ...updatedRequest, requesterId: user.id };
      
      // ‰ΩøÁî®Êï∞ÊçÆÊúçÂä°Êõ¥Êñ∞
      await DataService.updateRequest(updatedRequest.id, requestWithUser);
      
      // Êõ¥Êñ∞Êú¨Âú∞Áä∂ÊÄÅ
      setRequests(prev => prev.map(req => 
        req.id === updatedRequest.id ? requestWithUser : req
      ));
      
      setEditingRequest(null);
      
    } catch (error) {
      console.error('Êõ¥Êñ∞ËØ∑Ê±ÇÂ§±Ë¥•:', error);
      alert(t('error.updateRequestFailed') || 'Êõ¥Êñ∞ËØ∑Ê±ÇÂ§±Ë¥•');
    }
  };

  // Â§ÑÁêÜÂà†Èô§ËØ∑Ê±Ç
  const handleDeleteRequest = async (requestId) => {
    if (window.confirm(t('common.confirm') + 'Ôºü')) {
      try {
        // ‰ΩøÁî®Êï∞ÊçÆÊúçÂä°Âà†Èô§
        await DataService.deleteRequest(requestId);
        
        // Êõ¥Êñ∞Êú¨Âú∞Áä∂ÊÄÅ
        setRequests(prev => prev.filter(req => req.id !== requestId));
        
      } catch (error) {
        console.error('Âà†Èô§ËØ∑Ê±ÇÂ§±Ë¥•:', error);
        alert(t('error.deleteRequestFailed') || 'Âà†Èô§ËØ∑Ê±ÇÂ§±Ë¥•');
      }
    }
  };

  return (
    <div className="page-container">
      <Navbar userType="pin" user={user} />
      <div className="main-content">
        <div className="dashboard">
          <div className="dashboard-header">
            <div className="header-content">
              <h1 className="dashboard-title">{t('pin.dashboard.title')}</h1>
              <p className="dashboard-subtitle">{t('pin.dashboard.welcome', { name: user?.name })}</p>
            </div>
            <button 
              className="btn btn-primary"
              onClick={() => setShowCreateForm(true)}
            >
              <span className="btn-icon">+</span>
              {t('pin.dashboard.createRequest')}
            </button>
          </div>
          
          <div className="stats-grid">
            {stats.map((stat, index) => (
              <div key={index} className={`stat-card ${stat.color}`}>
                <div className="stat-header">
                  <div className="stat-info">
                    <div className="stat-title">{stat.title}</div>
                    <div className="stat-value">{stat.value}</div>
                  </div>
                  <div className={`stat-icon ${stat.color}`}>
                    {stat.icon}
                  </div>
                </div>
              </div>
            ))}
          </div>
          
          <div className="content-grid">
            <div className="card">
              <div className="card-header">
                <h3 className="card-title">{t('pin.dashboard.myRequests')}</h3>
                <p className="card-subtitle">{t('pin.dashboard.manageRequests')}</p>
              </div>
              <div className="card-body">
                {requests.length > 0 ? (
                  <div className="request-list">
                    {requests.map(request => (
                      <div key={request.id} className="request-item">
                        <div className="request-header">
                          <h4 className="request-title">{request.title}</h4>
                          <div className={`status-badge ${request.status}`}>
                            {request.status === 'matched' ? t('pin.dashboard.status.matched') : t('pin.dashboard.status.waiting')}
                          </div>
                        </div>
                        
                        <div className="request-meta">
                          <span className="category-tag">{t(DataService.getCategoryById(request.category).name)}</span>
                          <span className="meta-item">üëÅÔ∏è {request.viewCount || 0}</span>
                          <span className="meta-item">‚≠ê {request.shortlistCount || 0}</span>
                          <span className="meta-item">{DataService.getTimeAgo(request.createdAt, t)}</span>
                        </div>

                        {request.status === 'matched' && request.volunteer && (
                          <div className="volunteer-info">
                            <div className="volunteer-avatar">üë§</div>
                            <div className="volunteer-details">
                              <div className="volunteer-name">{t('pin.dashboard.matchedVolunteer', { name: request.volunteer })}</div>
                              <div className="volunteer-contact">{t('pin.dashboard.contactVolunteer')}</div>
                            </div>
                          </div>
                        )}

                        <div className="request-actions">
                          <button 
                            className="btn btn-secondary"
                            onClick={() => handleViewDetail(request)}
                          >
                            {t('pin.dashboard.viewDetails')}
                          </button>
                          <button 
                            className="btn btn-primary"
                            onClick={() => handleEditRequest(request)}
                          >
                            {t('pin.dashboard.edit')}
                          </button>
                          <button 
                            className="btn btn-danger btn-sm"
                            onClick={() => handleDeleteRequest(request.id)}
                            style={{ marginLeft: '0.5rem' }}
                          >
                            {t('pin.dashboard.delete')}
                          </button>
                        </div>
                      </div>
                    ))}
                  </div>
                ) : (
                  <div className="empty-state">
                    <div className="empty-state-icon">üìù</div>
                    <div className="empty-state-title">{t('pin.dashboard.noRequests')}</div>
                    <div className="empty-state-description">
                      {t('pin.dashboard.createFirst')}
                    </div>
                    <button 
                      className="btn btn-primary"
                      onClick={() => setShowCreateForm(true)}
                    >
                      ‚ûï {t('pin.dashboard.createMyFirst')}
                    </button>
                  </div>
                )}
              </div>
            </div>
            
            <div className="card">
              <div className="card-header">
                <h3 className="card-title">{t('pin.dashboard.quickActions')}</h3>
              </div>
              <div className="card-body">
                <div className="quick-actions">
                  <button 
                    className="quick-action-btn"
                    onClick={() => setShowCreateForm(true)}
                  >
                    ‚ûï {t('pin.dashboard.createNew')}
                  </button>
                  <a href="/pin/history" className="quick-action-btn">
                    üìä {t('pin.dashboard.viewHistory')}
                  </a>
                  <a href="/help" className="quick-action-btn">
                    ‚ùì {t('pin.dashboard.help')}
                  </a>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
      
      {showCreateForm && (
        <CreateRequestForm 
          onClose={() => {
            setShowCreateForm(false);
            setEditingRequest(null);
          }}
          onSubmit={editingRequest ? handleUpdateRequest : handleCreateRequest}
          initialData={editingRequest}
          isEditing={!!editingRequest}
        />
      )}

      {showRequestDetail && (
        <RequestDetailModal 
          request={showRequestDetail}
          onClose={() => setShowRequestDetail(null)}
        />
      )}
    </div>
  );
};

// Á°¨ÁºñÁ†ÅÂáΩÊï∞Â∑≤ÁßªÈô§ÔºåÁé∞Âú®‰ΩøÁî®DataServiceÂíåÂõΩÈôÖÂåñ

export default PINDashboard;
